import React, {useState, useEffect} from 'react';
import {Link, useNavigate} from 'react-router-dom';
import CommonTable from '../../components/table/CommonTable';
import CommonTableColumn from '../../components/table/CommonTableColumn';
import CommonTableRow from '../../components/table/CommonTableRow';
import {postList} from '../../Data';
import styled from "styled-components";
import Pagination from "react-js-pagination";
import './Paging.css';
import search from "../../components/Nav/images/search.svg";




// STYLE ------------------------------
// 검색창+글쓰기 버튼 감싸는 박스
const WriteSection = styled.section`
    display: flex;
    justify-content: center;
    padding-top: 74px;
`;


// 검색창
// const SearchInput = styled.input`
//     display: flex;
//     justify-content: center;
//
//     width: 1024px;
//     padding-bottom: 16px;
//     padding-left: 46px;
//     border-top: none;
//     border-right: none;
//     border-left: none;
//     border-image: initial;
//     border-bottom: 1px solid rgb(176, 184, 193);
//     outline: none;
//     caret-color: rgb(49, 130, 246);
//     font-size: 22px;
//     font-weight: 400;
//     line-height: 130%;
//     color: rgb(78, 89, 104);
//     min-height: 32px;
// `;







//공지사항(게시판) 리스트
const PostList = ({postsPerPage, totalPosts, paginate}) => {

    const navigate = useNavigate();

    //검색---------------------------
    const handleSearchInput = e => setSearchInput(e.target.value);
    const [searchInput, setSearchInput] = useState('');

    // useEffect(() => {
    //     window.addEventListener('click', function (e) {
    //         if (e.target.contains !== searchWrapper) {
    //             setSearchInput('');
    //         }
    //     });
    // }, []);


    //페이지네이션 ---------------------------
    const [page, setPage] = useState(1);

    const handlePageChange = (page) => {
        setPage(page);
    };

    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
        pageNumbers.push(i);
    }


    //★연습용 Data.js ---------------------------
    const [dataList, setDataList] = useState([]);

    useEffect(() => {
        setDataList(postList);
    }, [])



    return (

        <Wrap>
            <WriteSection>

                {/*  검색창 */}
                <SearchWrapper ref={searchWrapper}>
                    {searchInput && (
                        <RecommendSearch>
                            {filteredRecommendData.map(item => {
                                return (
                                    <SearchedLink
                                        onClick={() => {
                                            navigate(`/movieDetail/${item.id}`);
                                        }}
                                        key={item.id}
                                    >
                                        {item.name}
                                    </SearchedLink>
                                );
                            })}
                        </RecommendSearch>
                    )}
                    <SearchInput
                        placeholder="영화 이름 입력"
                        onChange={handleSearchInput}
                        value={searchInput}
                        scrollposition={scrollPosition}
                    />

                    <SearchIcon alt="serachIcon" src={search} />
                </SearchWrapper>


                {/*<SearchInput*/}
                {/*    type="text"*/}
                {/*    className="bottom_search_text"*/}
                {/*    placeholder="무엇이든 찾아보세요"*/}
                {/*    onChange={handleSearchInput}*/}
                {/*/>*/}

                {/* 버튼*/}
                {/*<SearchButton*/}
                {/*    type="button"*/}
                {/*    className="search_icon"*/}
                {/*    onClick=""*/}
                {/*    onChange=""*/}
                {/*>*/}
                {/*    <i className="fas fa-search"></i>*/}
                {/*</SearchButton>*/}

            </WriteSection>


            <Header>
                <h2 className="name-notice"/>공지사항


                {/* <!-- 글쓰기 버튼 --> */}
                <button
                    className="botom_write"
                    type="button"
                    onClick={() => {
                        navigate(`/`);
                    }}
                >
                    <a>글쓰기</a>
                </button>
            </Header>




            <div className="step-bar">
                <span className="gradation-blue"></span>
            </div>
            <CommonTable headersName={['글번호', '제목', '등록일', '조회수']}>
                {dataList ? dataList.map((item, index) => {
                    return (<CommonTableRow key={index}>
                        <CommonTableColumn>{item.no}</CommonTableColumn>
                        <CommonTableColumn>
                            <Link to={`/postView/${item.no}`}>{item.title}</Link>
                        </CommonTableColumn>
                        <CommonTableColumn>{item.createDate}</CommonTableColumn>
                        <CommonTableColumn>{item.readCount}</CommonTableColumn>
                    </CommonTableRow>)
                }) : ''}
            </CommonTable>


            <Pagination
                className="pagination"
                activePage={page} // 현재 페이지
                itemsCountPerPage={1} // 한 페이지랑 보여줄 아이템 갯수
                totalItemsCount={10} // 총 아이템 갯수
                pageRangeDisplayed={10} // paginator의 페이지 범위
                prevPageText={"‹"} // "이전"을 나타낼 텍스트
                nextPageText={"›"} // "다음"을 나타낼 텍스트
                onChange={handlePageChange} // 페이지 변경을 핸들링하는 함수
            />

        </Wrap>);
};
export default PostList;






// 검색창 스타일
const SearchWrapper = styled.div`
  position: relative;
  display: flex;
  flex-direction: row;
  align-items: center;
    margin-inline-start: auto;
    margin-right: 30px;
`;


// 검색창 아래 하단
const RecommendSearch = styled.div`
  position: absolute;
  display: flex;
  flex-direction: column;
    
    padding: 20px;
  top: 40px;
  width: 600px;
    height: auto;

    background: rgba(11, 11, 13, 0.1);
  //border-bottom-radius: 20px;
  //  background: rgba(225, 225, 255, 0.2);
  //  backdrop-filter: blur(20px);
  filter: drop-shadow(10px 10px 4px rgba(11, 11, 13, 0.1));
  animation: ${downFadeAnimation} 0.4s ease-out;

    background: ${props =>
    props.scrollPosition > 100 ? 'rgba(11, 11, 13, 0.1)' : '#fff'};
`;


const SearchedLink = styled(Link)`
  margin-top: 20px;
  font-size: 15px;
  text-decoration: none;
  color: gray;
`;

const SearchInput = styled.input`
  width: ${props => (props.scrollposition > 100 ? '600px' : '300px')};
  height: 40px;
  border: 0px;
  border-radius: 30px;
  margin-right: 20px;
  padding-left: 20px;
  background: #e4e4e4;
  outline: none;
  transition: 0.3s ease-out;
  transition-delay: 0.1s;

    
  //  검색창 클릭했을 떄
  &:focus {
    width: 600px;
      font-size: 14px;
      font-weight: 500;
    color: ${props => (props.scrollposition > 100 ? '#000' : 'white')};
    
      background: ${props =>
    props.scrollPosition > 100 ? '#fff' : 'rgba(249, 249, 249, 0.1)'};
    transition: 0.3s ease-out;
  }
`;

const SearchIcon = styled.img`
  position: absolute;
  right: 40px;
  width: 20px;
  cursor: pointer;
`;

const IconImg = styled.img`
  width: 25px;
  cursor: pointer;
  filter: ${props =>
    props.scrollposition > 100 ? 'invert(0%)' : 'invert(100%)'};
`;

const IconWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-right: 100px;
  gap: 30px;
`;



const Header = styled.div`
    font-family: 'SUIT-Regular' !important;
    color: rgb(51, 61, 75);
    font-size: 36px;
    font-weight: 800;
    margin-bottom: 48px;
    padding-top: 74px;
    text-align: left;
    width: 1044px;
    margin: 0 auto;
    margin-bottom: 48px;

    .name-notice {
        display: flex;
        justify-content: center;
    }
    //글쓰기 버튼
    .botom_write{
    width: 100px;
    height: 50px;
        border: 1px solid #cccccc;
        float: right;
        border-radius: 5px;
        margin-top: 2px;
        background-color: #e5e8eb;
        
        &:hover {
            cursor: pointer;
            //border: 2px solid rgb(51, 61, 75);
            background-color:  #3182f6;;
        }
    }
    
    .botom_write a{
        font-size: 16px;
        color: #0f2027;
        //padding: 10px 25px;
        text-align: center;
        display: flex;
        justify-content: center;
        cursor: pointer;
        color: rgb(51, 61, 75);
        
        &:hover {
            cursor: pointer;
            color: #fff;
           font-weight: 600;
        }
    }
`;

const Wrap = styled.div`
    width: 100%;
    height: 100vh;
    position: relative;
    margin: 0 auto;
    //padding: 40px 40px;
    //background: rgb(255,255,255);
    background: #fff;
    display: inline-block;
`;


//푸터 전체 - 위치/배경
const FooterTag = styled.footer`
    color: #6b7684;
    display: flex;
    align-items: center;
    flex-direction: column;
    margin-left: auto;
    margin-right: auto;
    padding: 26px 0;
    text-align: center;
    bottom: 0;
    width: 100%;
    z-index: 9999999 !important;;
    background-color: rgb(11, 11, 13) !important;
    border-top: 1px solid rgb(25, 31, 40);
`;
