import React, { useState } from "react";
import styled from "styled-components";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faApple } from "@fortawesome/free-brands-svg-icons";
import { faBars, faUser, faTimes } from "@fortawesome/free-solid-svg-icons";
import logo from "./images/rock_w_logo.svg";
import * as PropTypes from "prop-types";



//
const nav= () =>  {

    // const [show, setShow] = useState(false);
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [isToggled, setIsToggled] = useState(false);
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [userToggled, setUserToggled] = useState(false);



    return (
        <Header isToggled={isToggled} userToggled={userToggled}>

            {/* 햄버거 버튼(bar) */}
            <div className="toggle"
                onClick={() => {
                    setIsToggled(!isToggled);
                }}
            >
                <FontAwesomeIcon icon={!isToggled ? faBars : faTimes} />
            </div>


            {/* Apple 로고 */}
            <div className="logo">
                <img alt="logo" src={logo} onClick={() => (window.location.href = "/")} />
            </div>

            {/* User 버튼 */}
            <div
                className="user"
                onClick={() => {
                    setUserToggled(!userToggled);
                }}
            >
                <FontAwesomeIcon icon={!userToggled ? faUser : faTimes} />
            </div>

            {/* 메뉴 리스트 */}
            <ul className="header__menulist">
                <li>HOME</li>
                <li>Movie</li>
                <li>공지사항</li>
                <li></li>
                <li>Music</li>
                <li>고객지원</li>
            </ul>

            {/* User 메뉴 리스트 */}
            <ul className="header__right">
                <li>Login</li>
                <li>Register</li>
            </ul>
        </Header>
    );
}
export default nav;



// style ----------------------------------

//헤더 전체
const Header = styled.div`
    //헤더 고정
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 999;
    overflow: hidden;
    
    //헤더 사이즈
  max-width: 100%;
    max-height: 70px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
    user-select: none;
    
    //헤더 배경 스타일
    //background-color: black;
    backdrop-filter: saturate(180%) blur(20px);
    //background: rgba(225, 225, 255, 0.1);
    background: radial-gradient(circle at 20% 95%, #056877, #051828 96%);
    
    //폰트 스타일
  color: white;


    //로고 이미지
    img {
        width: 125px;
    }

    .logo {
        margin: 0 1rem;
        font-size: 2rem;
        width: 110px;
        text-align: center;
        argin-top: 8px;
    }
  .header__menulist {
    list-style: none;
    display: flex;
  }

  .header__left {
    display: flex;
  }

  .header__right {
    list-style: none;
    display: flex;
  }

  .header__right div {
    margin: 0 1rem;
  }

  li {
    padding: 0 1rem;
  }

  .toggle {
    display: none;
    font-size: 1.5rem;
    padding: 1rem 1rem;
  }

  .user {
    display: none;
    font-size: 1.5rem;
    padding: 1rem 1rem;
  }

//헤더 반응형 시 -     
  @media screen and (max-width: 768px) {
    flex-wrap: wrap;
      
    .header__right {
      display: ${(props) => (props.userToggled ? "flex" : "none")};
      flex-direction: column;
      width: 100%;
      background-color: black;
    }

    .header__menulist {
      display: ${(props) => (props.isToggled ? "flex" : "none")};
      flex-direction: column;
      width: 100%;
      background-color: black;
        padding: 35px 20px;
    }

    .header__menulist li,
    .header__right li {
      margin: 1rem 0;
      padding: 0;
    }
      //로고 이미지
      //img {
      //    width: 110px;
      //    text-align: center;
      //}  
    
      .header__right{
          padding: 35px 20px;
      }
    .toggle {
      display: block;
    }

    .user {
      display: block;
    }
  }
`;
