import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';


//연결
import Nav from "../../components/Nav/Nav";
import Footer from "../../components/Footer/Footer";


//메인 페이지
import MainTop from "./MainBannerSection/MainTop";



function Main() {
    return (
        <>
            <Nav />
            
            {/*<DimmedBackground></DimmedBackground>*/}

            <Contents>
                <MainTop></MainTop>

                {/*       <SlideSection*/}
                {/*title="첫구매, 인기작품 100원부터"*/}
                {/*contents={<WelcomeDeal welcomeDealData={welcomeDealData.results} />}/>*/}

                {/*      <SlideSection*/}
                {/*          title="상품"*/}
                {/*          contents={<TodayProduct productData={productData.results} />}*/}
                {/*      />*/}


            </Contents>
            <Footer/>
        </>
    );
}


const Contents = styled.div`
    padding-bottom: 64px;
    //background: #fff;
`;
const DimmedBackground = styled.div`
    display: none;
  position: fixed;
  width: 100%;
  height: 100vh;
  background-color: #333;
  opacity: 0.5;
  z-index: 150;
`;
export default Main;
