import React, {useState} from 'react';
import './css/FindIdPassword.css';
import styled from "styled-components";



function FindIdPassword() {

    //변수
    const [activeIndex, setActiveIndex] = useState(0);

    //핸들러
    const tabClickHandler = (index) => {
        setActiveIndex(index);
    };

    //탭
    const tabContArr = [{
        tabTitle: (<li className={activeIndex === 0 ? "is-active" : ""} onClick={() => tabClickHandler(0)}> 탭1 </li>),
        tabCont: (<div> 탭1 내용 </div>)
    }, {
        tabTitle: (<li className={activeIndex === 1 ? "is-active" : ""} onClick={() => tabClickHandler(1)}> 탭2 </li>),
        tabCont: (<div> 탭2 내용 </div>)
    }];


    //HTML
    return (
        <WrapBody>
            <Wrap>
            <Ul className="TabMenu">
                    {tabContArr.map((section, index) => {
                        return section.tabTitle
                    })}
            </Ul>

                <Div>
                    {tabContArr[activeIndex].tabCont}
                </Div>
            </Wrap>
        </WrapBody>
    );
}
export default FindIdPassword;



// style --------------------------
const WrapBody = styled.div`
    width: 100%;
    //height: 100vh;
    position: relative;
    margin: 0 auto;
    //padding: 40px 40px;
    //background: rgb(255,255,255);
    background: #fff;
    display: inline-block;
`;


// 전체 박스
const Wrap = styled.div`
    display: flex;
    justify-content: center;
    width: 1024px;
    margin: 0 auto;
    padding: 60px 40px;
    margin-top: 20px;
`;

const Ul = styled.ul`
    list-style: none;
    margin-bottom: 20px;
    margin-top: 10px;
    display: flex;
    flex-direction: row;
    align-items: center;


    //디자인- 비활성화
    color: #000;
    font-weight: 300;
    text-align: center;
    display: flex;
    justify-content: center;
    font-size: 16px;

    //탭메뉴

    .activeIndex {
        // 기본 Tabmenu 에 대한 CSS를 구현
        width: 300px;
        heigth: 30px;
        padding: 10px;
        transition: 0.5s;
        border: 1px solid rgba(255, 255, 255, 0.2);
        outline: none;
        cursor: pointer;
        color: #fff;
        font-weight: 300;
        text-align: center;
        display: flex;
        justify-content: center;
        font-size: 16px;
`;

const Div = styled.div`
    display: flex;
    justify-content: center;
    width: 1200px;
    height: 400px;
    margin: 0 auto;
    background-color: red;
    margin-bottom: 20px;
    padding: 80px 40px;
`;
