import React from 'react';
import {Link, NavLink, useLocation} from 'react-router-dom';

// import axios from "axios";
// import React, { useState, useEffect } from "react";

import styled from "styled-components";
import top from "./images/top.png";
import SideBar from "./SideBar";
import {Button} from "react-bootstrap";
import axios from "axios";


function MyPage() {
    //
    const pathName = useLocation().pathname;

    //
    let body = {

    };

    //
    const onSubmit = async () => {
        try {
            const response = await axios.post(
                "https://backend.alittlevanilla.kro.kr/member/signup",
                body,
                {
                    headers: { "Content-Type": "application/json" },
                }
            );
            console.log(response.data.code);
            if (response.data.code === 1000) {
                window.location.href = "/emailcheck";
            }
        } catch (e) {
            console.log(e);
        }
    };


    return (<>
            <SideBar/>
            <Wrap>

                    <TopContainer>
                        {/*<img className="top-img" src={Mypage}/>*/}
                        <Section>
                            {/*<Article>*/}

                                <ProfileBoxContainer>
                                    <TopImg src={top}></TopImg>
                                    <TitelBox>홍길동 님<br />
                                        <div className="my">나의 이용권</div>
                                    </TitelBox>
                                </ProfileBoxContainer>


                                {/*문구 */}
                                <TitelBoxContainer>


                                    {/*<FormBlockBody>*/}
                                    {/*    <InputTextSizeW>*/}
                                    {/*        <EmailsButton*/}
                                    {/*            type="button"*/}
                                    {/*            onClick={() => {*/}
                                    {/*                onSubmit();*/}
                                    {/*            }}*/}
                                    {/*        >*/}
                                    {/*            프로필 전환*/}
                                    {/*        </EmailsButton>*/}
                                    {/*    </InputTextSizeW>*/}
                                    {/*</FormBlockBody>*/}


                                    {/*<Titel>나의 이용권</Titel>*/}

                                </TitelBoxContainer>
                            {/*</Article>*/}
                        </Section>
                    </TopContainer>
            </Wrap>
        </>);
}

export default MyPage;


//전체 박스
const Wrap = styled.div`
    width: 100%;
    height: 100vh;
`;
//전체 감싸는 프로필 박스
const TopContainer = styled.div`
    width: 100%;
    height: 280px;
    position: relative;
    grid-template-columns: 10rem minmax(auto, 100%) min-content;
    column-gap: 2rem;
    padding: 40px 40px;
    background-color: rgb(25, 25, 25);
`;
// 프로필 감싸는 박스
const Section = styled.div`
    width: 1024px;
    margin: 0 auto;
    margin-top: 22px;
    margin-bottom: 22px;
`;


// 프로필 박스
const ProfileBoxContainer = styled.div`

    display: flex;
    align-items: flex-start;
    gap: 30px;
    border-right: 1px solid #1b1b1b;
    font-size: 0;
    min-height: 96px;
    align-items: center;
`;

// 프로필
const TopImg = styled.img`
    width: 150px;
   float: left;
    border: 5px solid rgba(255,255,255,0.2);
`;

//홍길동 박스
const TitelBox = styled.div`
    position: relative;
    display: flex;
    flex-direction: column;
    color: #fff;
    font-family: 'SUIT-Regular' !important;
    font-size: 28px;
    text-align: left;
    font-weight: 500;
    font-stretch: normal;
    font-style: normal;
    letter-spacing: normal;
    text-align: left;
    z-index: 1;
    
    .my{
        color: #fff;
        font-size: 14px;
        display: flex;
        flex-direction: end;
    }
`;


//홍길동 박스
const TitelBoxs = styled.div`
    position: relative;
    display: flex;
    align-content: end;
    color: #fff;
    font-family: 'SUIT-Regular' !important;
    font-size: 12px;
`;

//문구 감싸는 박스
