import styled from "styled-components";
import React, { useEffect } from "react";
import MyWatchedMovieCard from "./MyWatchedMovieCard";
import { useSelector, useDispatch } from "react-redux";
import { getMypage } from "../../redux/modules/movies";
import { useNavigate } from "react-router-dom";

const MyWatchedMovie = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { isLoading, error, mylist } = useSelector((state) => state.movies);
  const login = localStorage.getItem("token"); // token 유무로 로그인 상태 판별하기
  useEffect(() => {
    login ? dispatch(getMypage()) : navigate("/login"); //로그인이 true이면 dispatch로 mylist를 불러오고 아니면 로그인페이지로 보낸다.
  }, []);

  if (isLoading) {
    return <div>로딩 중....</div>;
  }

  if (error) {
    return <div>{error.message}</div>;
  }
  return (
    <div>
      <Container>
        <Contents>
          {/*사이드바 start*/}
          <ColsContent>
            <ColSideBar>
              <Profile>
                <ProfileInner>
                  <ProfileContents>
                    <ImageBox>
                      <ThumbImage></ThumbImage>
                    </ImageBox>
                    <BoxContent>
                      {/* 마이페이지 유저 이름 */}
                      <Strong>{mylist?.name}</Strong>
                      <Div></Div>
                    </BoxContent>
                  </ProfileContents>
                </ProfileInner>
              </Profile>
              <LikeBtn
                onClick={() => {
                  navigate("/likelist");
                }}
              >
                {/*좋아요 누른 영화 숫자 들어갈 자리*/}
                <Howmany>{mylist?.countLike}</Howmany>
                {/*좋아요 누른 영화 숫자 들어갈 자리*/}
                <P>기대되는 영화</P>
              </LikeBtn>
              <WatchedBtn
                onClick={() => {
                  navigate("/movielist");
                }}
              >
                {/*내가 본 영화 숫자 들어갈 자리*/}
                <Howmany>{mylist?.countMovie}</Howmany>
                {/*내가 본 영화 숫자 들어갈 자리*/}
                <P>내가 본 영화</P>
              </WatchedBtn>
            </ColSideBar>
          </ColsContent>
          {/*사이드바 finish*/}
          <LikedMovies>
            <LikedTitle>
              내가 본 영화<Howmany2>{mylist?.countMovie}건</Howmany2>
              <Select name="WatchedMovies" id="WatchedMovies">
                {/*내가 본 영화들 년도 들어갈 자리*/}
                <option value="등록일 순">전체</option>
                <option value="등록일 순">??</option>
                {/*내가 본 영화들 년도 들어갈 자리*/}
              </Select>
              {/*영화 년도 선택 오름차순*/}
              <GoBtn>GO</GoBtn>
              {/*영화 년도 선택 오름차순*/}
              <RegisterBtn>실관람객 등록</RegisterBtn>
            </LikedTitle>
            <WatchedListDiv>
              <WatchedListContainer>
                {mylist?.movies?.map((movie) => (
                  <MyWatchedMovieCard movie={movie} key={movie.movieId} /> //map돌린 결과물 movie를 자식컴포넌트로 전달
                ))}
              </WatchedListContainer>
            </WatchedListDiv>
          </LikedMovies>
        </Contents>
      </Container>
    </div>
  );
};

export default MyWatchedMovie;
const Container = styled.div`
  width: 100%;
  height: 100%;
`;
const Contents = styled.div`
  width: 980px;
  height: 100%;
  margin: 0 auto;
`;
const ColsContent = styled.div`
  padding-top: 25px;
`;
const ColSideBar = styled.div`
  width: 160px;
  height: 527px;
  float: left;
`;
const Profile = styled.div`
  width: 160px;
  height: 241px;
  border-radius: 5px;
  position: relative;
  margin-top: 10px;
  border: solid #bab8ae 1px;
`;
const ProfileInner = styled.div`
  position: relative;
  margin-top: 0;
`;
const ProfileContents = styled.div`
  padding: 12px 12px 20px 12px;
  color: #222;
  width: 158px;
  height: 239px;
`;
const ImageBox = styled.div`
  position: relative;
  width: 135px;
  height: 164px;
`;
const ThumbImage = styled.div`
  width: 135px;
  height: 135px;
  display: block;
  position: relative;
  background-image: url("	http://img.cgv.co.kr/R2014/images/common/default_profile.gif");
  background-size: cover;
