  display: inline-block;
  width: 12px;
  height: 14px;
  margin: 0 0 0 5px;
  background-image: url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTziH2tgX4B5vCYQGtAGQgMVkGAJFmJjeDKuw&usqp=CAU");
  font: 0px/0 a;
  background-size: cover;
  vertical-align: top;
`;
const Em = styled.em`
  margin: 3px 0 10px;
  font-family: verdana, sans-serif;
  font-size: 10px;
  font-weight: bold;
  width: 134px;
  height: 12px;
  display: block;
  font-style: normal;
`;
const LikeBtn = styled.button`
  margin-left: 1px;
  margin-top: 10px;
  width: 158px;
  height: 80px;
  border: 1px solid #bebebe;
  border-radius: 5px;
  color: #333;
  background: none;
  cursor: pointer;
`;
