  background: #f34949;
  border: none;
  color: white;
  font-weight: 900;
  text-align: center;
  vertical-align: middle;
`;
const WatchedListDiv = styled.div`
  margin-top: 15px;
  clear: both;
  zoom: 1;
  width: 720px;
  height: 300px;
`;
const WatchedListContainer = styled.ul`
  list-style: none;
`;
