import React from "react";
import "./css/sidebar.css";
import styled from "styled-components";
import {Link, useLocation} from "react-router-dom";
import SidebarItem from "./SidebarItem";
import profile from "./images/profile.svg";


// 마이페이지
function MyPage() {

    // URL의 path값을 받아올 수 있다.
    const pathName = useLocation().pathname;

    const menus = [{name: "내가 찜한 리스트", path: "/"}, {name: "내가 작성한 리뷰", path: "/members"}, {
        name: "개인 정보 수정", path: "/cashes"
    }];

    return (<Wrap>
            <Side>
                <SideWrap>
                    <Profile src={profile}></Profile>
                    <Menu>
                        {menus.map((menu, index) => {
                            return (<Link to={menu.path} key={index}>
                                <SidebarItem
                                    menu={menu}
                                    isActive={pathName === menu.path ? true : false} // 현재 URL pathname과 객체에 담긴 path값 일치 여부 확인
                                />
                            </Link>);
                        })}
                    </Menu>
                </SideWrap>
            </Side>
        </Wrap>);
}

export default MyPage;


// 폼 디자인
const Wrap = styled.div`
    width: 100%;
    height: auto;
    background-color: #fff;
`;

const Side = styled.div`
    border-right: 1px solid #e0e0e0;
    width: 15%;
    height: 1080px;
    background-color: #fff;
`;

// 폼 디자인
const SideWrap = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 120px 40px;
`;


const Menu = styled.div`
    margin-top: 30px;
    width: 200px;
    display: flex;
    flex-direction: column;
    font-family: 'SUIT-Regular' !important;
    font-size: 18px;
    font-weight: 500;
    cursor: pointer;
    text-align: left;
    color: #0f2027 ! important;
`;

const Profile = styled.img`
    width: 135px;
    //height: 90px;
    border-radius: 100%;
`;
