import axios from "axios";
import React, { useState } from "react";
import styled from "styled-components";


// Login-로그인
function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    let body = {
        username: email,
        password: password,
    };

    const onSubmit = async () => {
        try {
            const response = await axios.post(
                "https://backend.alittlevanilla.kro.kr/member/authenticate",
                body,
                {
                    headers: { "Content-Type": "application/json" },
                }
            );

            console.log(response.data.token);

            if (response.data.token !== null) {
                localStorage.clear();
                localStorage.setItem("token", response.data.token);
                console.log("response.data.token:  " + response.data.token);

                window.location.replace("/");
            } else {
                setEmail("");
                setPassword("");
                localStorage.clear();
            }
        } catch (e) {
            alert(
                "회원 정보가 일치 하지 않습니다. 아이디와 비민번호를 다시 확인해 주세요!"
            );
            console.log(e);
        }
    };
//html
    return (
        <>
            <LoginWrap>
                <LoginContainer>
                    <LoginSigninContent>

                        <BorderAndText>
                            <LoginHeadText>로그인<br/>
                                <span>Rock 계정으로 로그인</span>
                            </LoginHeadText>
                        </BorderAndText>

                        <EmailLoginContainer>
                            <div>
                                <EmailLoginInput
                                    id="email"
                                    type="email"
                                    value={email}
                                    placeholder="이메일"
                                    required
                                    onChange={(e) => {
                                        setEmail(e.target.value);
                                    }}
                                />
                                <EmailLoginInput
                                    id="password"
                                    // type="password"
                                    value={password}
                                    placeholder="비밀번호"
                                    required
                                    onChange={(e) => {
                                        setPassword(e.target.value);
                                    }}
                                />
                            </div>
                            <EmailLoginOption>
                                <div>
                                    <InputCheckbox>
                                        <Bp type="checkbox"></Bp>
                                    </InputCheckbox>
                                    <label>이메일 저장하기</label>
                                </div>
                                <a>아이디 / 비밀번호 찾기</a>
                            </EmailLoginOption>
                        </EmailLoginContainer>
                        <CommonButton
                            type="button"
                            onClick={() => {
                                onSubmit();
                                console.log("body: " + email + ", " + password);
                            }}
                        >
                            로그인
                        </CommonButton>
                    </LoginSigninContent>





                    {/*구글 인증 로그인*/}
                    <LoginSignupContent>
                        <BorderAndText>
                            <span>간편 로그인/회원가입</span>
                        </BorderAndText>

                        <VerticalButtons>
                            <BarButton
                                href="https://backend.alittlevanilla.kro.kr/oauth2/authorization/kakao"
                                className="kakao"
                            >
                                <SpIcon className="Kakaotalk"></SpIcon>
                                카카오로 3초만에 시작하기
                            </BarButton>
                            <BarButton className="email" href="/signupemail">
                                이메일로 가입하기
                            </BarButton>
                        </VerticalButtons>

                        <HorizontalButtons>
                            <RadiusButton
                                className="naver"
                                href="/w/naver/login?redirect_uri=https%3A%2F%2Fwww.idus.com%2Fw%2Fmain%2Ftoday-recommend-product"
                            >
                                <SpIcon className="naver"></SpIcon>
                            </RadiusButton>

                            <RadiusButton className="facebook" href="">
                                <SpIcon className="facebook"></SpIcon>
                            </RadiusButton>

                            <RadiusButton className="twitter" href="">
                                <SpIcon className="twitter"></SpIcon>
                            </RadiusButton>

                            <RadiusButton className="apple" href="">
                                <SpIcon className="apple"></SpIcon>
                            </RadiusButton>
                        </HorizontalButtons>
                    </LoginSignupContent>


                </LoginContainer>
            </LoginWrap>
        </>
    );
}



const CommonButton = styled.button`
  margin-top: 6px;
  width: 100%;
  height: 44px;
  border-radius: 2px;
  border: none;
  background: #f1c333;
  color: #ffffff;
  font-size: 16px;
  line-height: 30px;
  padding: 0 16px;
  display: inline-block;
  box-shadow: 0 1px 3px 0 rgb(220 220 220 / 30%);
  box-sizing: border-box;
  cursor: pointer;
  font-weight: 400;
  text-align: center;
  text-decoration: none;
  transition: border-color 0.2s cubic-bezier(0.075, 0.82, 0.165, 1);
  vertical-align: middle;
`;

const Bp = styled.input`
  -webkit-appearance: none;
  background: transparent;
  display: inline-block;
  position: relative;
  height: 18px;
  width: 18px;
  vertical-align: middle;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  border: 0;
  margin: 0;

  &:before {
    cursor: pointer;
    content: "";
    display: inline-block;
    line-height: 16px;
    width: 16px;
    height: 16px;
    background: #fff;
    position: absolute;
    top: 0px;
    left: 0px;
    border: 1px solid #acacac;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
    text-align: center;
  }
`;

const InputCheckbox = styled.div`
