import axios from "axios";
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {Button} from "react-bootstrap";




// SignUp-회원가입
function SignUp() {
    const [isAllChecked, setIsAllChecked] = useState(false);
    const [checkedItems, setCheckedItems] = useState([]);

    const allAgreeHandler = (checked) => {
        setIsAllChecked(!isAllChecked);
        if (checked) {
            setCheckedItems([...checkedItems, "provision", "privacy"]);
        } else if (
            (!checked && checkedItems.includes("provision")) ||
            (!checked && checkedItems.includes("privacy"))
        ) {
            setCheckedItems([]);
        }
    };

    const agreeHandler = (checked, value) => {
        if (checked) {
            setCheckedItems([...checkedItems, value]);
        } else if (!checked && checkedItems.includes(value)) {
            setCheckedItems(checkedItems.filter((el) => el !== value));
        }
    };


    //알림창
    const fake = () => {
        alert("현재 개인정보를 수집하고 있지 않습니다🙂 안심하고 테스트 해보세요.");
    };

    useEffect(() => {
        if (checkedItems.length >= 2) {
            setIsAllChecked(true);
        } else {
            setIsAllChecked(false);
        }
    }, [checkedItems]);


    //변수 설정-이메일/패스워드/닉네임
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [nickname, setNickname] = useState("");

    let body = {
        email: email,
        password: password,
        nickname: nickname,
    };

    //
    const onSubmit = async () => {
        try {
            const response = await axios.post(
                "https://backend.alittlevanilla.kro.kr/member/signup",
                body,
                {
                    headers: { "Content-Type": "application/json" },
                }
            );
            console.log(response.data.code);
            if (response.data.code === 1000) {
                window.location.href = "/emailcheck";
            }
        } catch (e) {
            console.log(e);
        }
    };

    // const onEmailAuth = async () => {
    //   console.log("onEmailAuth!");

    //   try {
    //     const response = await axios.post("https://backend.alittlevanilla.kro.kr/member/", body, {
    //       headers: { "Content-Type": "application/json" },
    //     });
    //   } catch (e) {
    //     console.log(e);
    //   }
    // };


    //이메일
    const emailCheck = async () => {
        if (email === "") {
            setCheckedEmail("필수 항목입니다.");
        } else {
            var regex =
                /^[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*\.[a-zA-Z]{2,3}$/i;
            console.log("비밀번호 유효성 검사 :: ", regex.test(email));



            if (regex.test(email)) {
                try {
                    const response = await axios.get(
                        "https://backend.alittlevanilla.kro.kr/member/" + email
                    );
                    if (response.data === true) {
                        setCheckedEmail("이미 존재하는 이메일 입니다.");
                    } else {
                        setCheckedEmail("가능");
                        console.log("가능");
                    }
                } catch (e) {
                    console.log(e);
                }
            } else {
                setCheckedEmail("이메일 주소를 확인해 주세요.");
            }
        }
    };

    const [checkedEmail, setCheckedEmail] = useState("");

    return (
        <WrapLogin>
            <HeadBannerGroup />
            <ReauthPhone>
                <LoginWrap>
                    <LoginLogo>
                        <h1>
                            {/* <LogoA href="/">
                <SpIcon />
              </LogoA> */}
                        </h1>
                    </LoginLogo>

                    <LoginSection>
                        <div className="step-bar">
                            <span className="gradation-blue"></span>
                        </div>
                        <LoginTitle>회원가입
                            <h5>아이디와 이메일로 간편하게 무빙을 시작하세요!</h5>
                        </LoginTitle>


                        {/*<SignupStep className="wrap">*/}
                        {/*    <ul>*/}
                        {/*        <li>1</li>*/}
                        {/*        <IsActive>2</IsActive>*/}
                        {/*    </ul>*/}
                        {/*    <Title>가입 정보 입력하기</Title>*/}
                        {/*</SignupStep>*/}


                        {/*아이디*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 아이디
                            </FormBlockHead>
                            <FormBlockBody>
                                <InputTextSizeW>
                                    <EmailInput
                                        id="email"
                                        type="email"
                                        value={email}
                                        placeholder="아이디를 입력해주세요."
                                        required
                                        onChange={(e) => {
                                            setEmail(e.target.value);
                                        }}
                                        onBlur={() => emailCheck()}
                                    />
                                </InputTextSizeW>
                                <FormError>{checkedEmail}</FormError>
                            </FormBlockBody>
                        </FormBlock>


                        {/*비밀번호*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 비밀번호
                            </FormBlockHead>
                            <FormBlockBody>
                                <InputTextSizeW>
                                    <EmailInput
                                        id="password"
                                        // type="password"
                                        value={password}
                                        placeholder="비밀번호 (영문+숫자+특수문자 8자 이상)"
                                        required
                                        onChange={(e) => {
                                            setPassword(e.target.value);
                                        }}
                                    />
                                </InputTextSizeW>
                            </FormBlockBody>
                            <FormBlockBody>
                                <InputTextSizeW>
                                    <EmailInput placeholder="비밀번호 확인" required/>
                                </InputTextSizeW>
                            </FormBlockBody>
                        </FormBlock>


                        {/*이름*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 이름
                            </FormBlockHead>
                            <FormBlockBody>
                                <InputTextSizeWTypeL>
                                    <EmailInput
                                        id="name"
                                        value={nickname}
                                        type="text"
                                        placeholder="이름을 입력해 주세요"
                                        required
                                        onChange={(e) => {
                                            setNickname(e.target.value);
                                        }}
                                    />
                                </InputTextSizeWTypeL>
                            </FormBlockBody>
                        </FormBlock>


                        {/*전화번호*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 전화번호
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailInput type="hidden" required/>
                                        <EmailInput
                                            type="tel"
                                            placeholder="010-1234-5678"
                                            data-auth="cell_phone"
                                            required
                                        />
                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>


                        {/*★★★★여기부터!!!!!!!!!!!!!!!!!!!*/}
                        {/*이메일 인증*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 이메일
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailsInput type="hidden" required/>
                                        <EmailsInput
                                            type="email"
                                            placeholder="이메일을 입력해주세요"
                                            data-auth=""
                                            required
                                        />
                                        <Button type="button" className="idbutton"
                                            onClick="">이메일 전송
                                        </Button>

                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>


                        {/*이메일 인증*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 인증코드
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailsInput type="hidden" required/>
                                        <EmailsInput
                                            type="email"
                                            placeholder="이메일을 확인해주세요"
                                            data-auth=""
                                            required
                                        />
                                        <Button type="button" className="idbutton"
                                                onClick="">인증 코드
                                        </Button>

                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>

                        
                        {/*생년월일*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 생년월일
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailInput type="hidden" required/>
                                        <EmailInput
                                            type="tel"
                                            placeholder="010-1234-5678"
                                            data-auth="cell_phone"
                                            required
                                        />
                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>


                        {/*성별*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 성별
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailInput type="hidden" required/>
                                        <EmailInput
                                            type="tel"
                                            placeholder="010-1234-5678"
                                            data-auth="cell_phone"
                                            required
                                        />
                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>




                        {/*모두 동의 합니다*/}
                        <FormBlockCheckAllWrap>
                            <Terms>
                                <TermsHead>
                                    <InputCheckBox>
                                        <input
                                            type="checkbox"
                                            value="agree"
                                            onChange={(e) => {
                                                allAgreeHandler(e.currentTarget.checked);
                                            }}
                                            checked={isAllChecked}
                                        />
                                    </InputCheckBox>
                                    <TermsLabel onClick={fake}>모두 동의합니다.</TermsLabel>
                                </TermsHead>

                                <TermsBody>
                                    <TermsItem>
                                        <InputCheckBox>
                                            {/* <Terms1 type="checkbox"></Terms1> */}
                                            <input
                                                type="checkbox"
                                                value="provision"
                                                onChange={(e) => {
                                                    agreeHandler(e.currentTarget.checked, e.target.value);
                                                }}
                                                checked={
                                                    checkedItems.includes("provision") ? true : false
                                                }
                                            />
                                        </InputCheckBox>
                                        <Terms1Label>만 14세 이상입니다.</Terms1Label>
                                    </TermsItem>
                                    {/*  */}
                                    <TermsItem>
                                        <InputCheckBox>
                                            {/* <Terms1 type="checkbox"></Terms1> */}
                                            <input
                                                type="checkbox"
                                                value="privacy"
                                                onChange={(e) => {
                                                    agreeHandler(e.currentTarget.checked, e.target.value);
                                                }}
                                                checked={
                                                    checkedItems.includes("privacy") ? true : false
                                                }
                                            />
                                        </InputCheckBox>
                                        <Terms2A onClick={fake}>이용약관 필수 동의</Terms2A>
                                    </TermsItem>
                                    {/*  */}
                                </TermsBody>
                            </Terms>

                            <Terms1Error/>
                            <TermsError/>
                        </FormBlockCheckAllWrap>


                        {/*회원가입 버튼*/}
                        <FormBlockSubmit>
                            <FormBlockBody>
                                <BtnLogin
                                    type="button"
                                    onClick={() => {
                                        onSubmit();
                                    }}
                                >
                                    가입하기
                                </BtnLogin>
                            </FormBlockBody>
                        </FormBlockSubmit>


                    </LoginSection>
                </LoginWrap>
            </ReauthPhone>
        </WrapLogin>
    );
