import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';



//SwiperCard
function SwiperCard({ movie }) {
    const [isMouseHover, setIsMouseHover] = useState(false);
    const {
        movieName,
        movieNameInEnglish,
        movieThumbnailImageUrl,
        movieSimpleDescription,
    } = movie;

    const navigate = useNavigate();

    const goToBooking = () => {
        navigate(`/booking`);
    };
    const onMouseOverMovie = () => {
        setIsMouseHover(true);
    };

    const onMouseOutMovie = () => {
        setIsMouseHover(false);
    };

    return (
        <SwiperCardContainer
            onMouseOver={onMouseOverMovie}
            onMouseOut={onMouseOutMovie}
        >
            <Overlay isMouseHover={isMouseHover}>
                <OverlayText>{movieSimpleDescription}</OverlayText>

                {/*마우스 호버 시 */}
                <MouseHoverButton>

                    <OverlayBtn
                        onClick={() => {
                            navigate(`/movieDetail/${movie.id}`);
                        }}
                    >
                        상세 보기
                    </OverlayBtn>
                    {/*<OverlayBtn onClick={goToBooking}>예약 하기</OverlayBtn>*/}

                </MouseHoverButton>
            </Overlay>
            <PosterImg src={movieThumbnailImageUrl} />
            {/*<MovieInfoTextWrapper>*/}
            {/*    <MovieInfoTitle>{movieName}</MovieInfoTitle>*/}
            {/*    <MovieInfoText>{movieNameInEnglish}</MovieInfoText>*/}
            {/*</MovieInfoTextWrapper>*/}
        </SwiperCardContainer>
    );
}
export default SwiperCard;



// style
const fadeIn = keyframes`
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
`;

const SwiperCardContainer = styled.div`
  position: relative;
`;

const Overlay = styled.div`
  display: ${props => (props.isMouseHover ? 'block' : 'none')};
  position: absolute;
  width: 300px;
  height: 430px;
  border-radius: 10px;
  background: rgb(254,107,12,0.5);
  backdrop-filter: blur(20px);
  animation: ${fadeIn} 0.1s ease-out;
  z-index: 100;
`;

// 2단, 호버시, 보이는 내용
const OverlayText = styled.p`
  width: 150px;
  margin: auto;
  margin-top: 100px;
  font-weight: 400;
  text-align: center;
  color: white;
  font-size: 14px;
  line-height: 25px;
`;

const OverlayBtn = styled.button`
  width: 150px;
  height: 55px;
    border-radius: 5px;
  margin-top: 30px;
  border: 0px;
  
  background: #FE6B0C;
  color: white;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;

    
  &:hover {
    background: white;
    color: #FE6B0C;
      border: 3px solid #FE6B0C;
    //box-shadow: 1.8px 3.7px 8px #767676;
  }
`;

const PosterImg = styled.img`
  width: 100%;
`;

const MovieInfoTextWrapper = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 30px;
`;

const MovieInfoTitle = styled.h3`
  font-weight: 700;
  font-size: 20px;
`;

const MovieInfoText = styled.span`
  margin-top: 10px;
  font-size: 15px;
  color: gray;
`;
const MouseHoverButton = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 75px;
`;
