import React from 'react';
import {Link, NavLink, useLocation} from 'react-router-dom';

import Router from "../../Router";
import styled from "styled-components";
import Mypage from "./images/Mypage.png";
import MemberInfo from "./MemberInfo";
import top from "./images/top.png";
import SideBar from "./SideBar";


function MyPage() {

    const pathName = useLocation().pathname;

    return (<>
            <SideBar/>
            <Wrap>
                <Section>
                    <TopContainer>
                        {/*<img className="top-img" src={Mypage}/>*/}
                        <TopImg src={top}></TopImg>

                    </TopContainer>
                </Section>


                {/*<MainContainer>*/}
                {/*    <TitleNo>내가 찜한 리스트</TitleNo>*/}
                {/*    <TitelBox></TitelBox>*/}
                {/*</MainContainer>*/}

                {/*<MainContainer>*/}
                {/*    <TitleNo>내가 작성한 리뷰</TitleNo>*/}
                {/*    <TitelBox></TitelBox>*/}
                {/*</MainContainer>*/}

                {/*<MainContainer>*/}
                {/*    <TitleNo>개인 정보 수정</TitleNo>*/}
                {/*    <TitelBox></TitelBox>*/}
                {/*</MainContainer>*/}
            </Wrap>
        </>);
}

export default MyPage;


//전체 박스
const Wrap = styled.div`
    width: 100%;
    height: 100vh;
`;
const Section = styled.div`
    width: 1024px;
    height: 100vh;
`;


const TopImg = styled.img`
    padding-left: 3.888rem;
    padding-right: 3.888rem;

`;


const TopContainer = styled.div`
    width: 100%;
    height: 180px;
    margin: 0 auto;
    position: relative;
    display: grid;
    grid-template-columns: 10rem minmax(auto, 100%) min-content;
    column-gap: 2rem;
    padding: 5rem 1.333rem;
    background-color: rgb(25, 25, 25);


`;


const MainContainer = styled.div`
    width: 1024px;
    position: relative;
    margin: 0 auto;
    display: flex;
    align-items: flex-start;
    margin-top: 80px;

`;

const TitleNo = styled.div`
    width: 200px;
    font-family: "Nanum Gothic", sans-serif;
    font-size: 1.2rem;
    color: rgba(255, 255, 255, 1);
    font-weight: 500;
    letter-spacing: -2px;
    line-height: 1.5;
    padding: 0 0 1rem;
    font-stretch: normal;
    font-style: normal;
    letter-spacing: normal;
    text-align: left;
    z-index: 1;
`;


const TitelBox = styled.div`
    width: 1024px;
    background-color: red;
`;
