import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';



// Videos
const Videos = ({ item, movieData }) => {
    const { id, title, description, video_url } = item;

    const navigate = useNavigate();

    const goToBooking = () => {
        navigate(`/`);
    };

    return (
        <VideoWrapper>
            <VideoTextWrapper>
                <VideoTitle>&#60; {title} &#62;</VideoTitle>

                <VideoContext>{description}</VideoContext>

                {/*재생 ? */}
                <VideoBtn
                    onClick={() => {
                        navigate(`/movieDetail/${id}`);
                    }}
                >
                    재생&nbsp; &#62;
                </VideoBtn>


                {/*상세정보 - 팝업창 띄울 것인지? */}
                <VideoBtn onClick="">! 상세 정보</VideoBtn>

            </VideoTextWrapper>

            <MainVideo
                alt="mainVideo"
                className="mainVideo"
                src={video_url}
                autoPlay
                muted
                loop
            />
        </VideoWrapper>
    );
};
export default Videos;




// 메인 동영상 스타일
const VideoWrapper = styled.div`
  position: relative;
  width: 100vw;
  //height: 500px;
  overflow: hidden;
`;

// ■메인 배너 슬라이드 문구
const VideoTextWrapper = styled.div`
  position: absolute;
  top: 225px;
    left: 120px;
  width: 600px;
  text-align: start;
  z-index: 10;
    
    
    //background-color: rgba(255,255,255,0.1);
    //backdrop-filter: blur(20px);
`;

// ■메인 배너 슬라이드 문구
const VideoTitle = styled.h2`
  color: white;
  font-size: 40px;
  font-weight: 600;
  text-shadow: 1px 1px 3px black;
  
`;

const VideoContext = styled.p`
  margin: 12px 0 50px 0;
  color: white;
  font-weight: 300;
  font-size: 17px;
  line-height: 25px;
  opacity: 0.9;
    
`;

const VideoBtn = styled.button`
  width: 120px;
  margin: 15px 10px;
  padding: 10px 10px;
  border: 2px solid #fff;
  border-radius: 5px;
  color: white;
  //background: #02d6e8;
  font-size: 15px;
  cursor: pointer;
  transition: 0.3s;

  &:hover {
    color: rgba(222, 222, 255, 0.19);
    background: rgba(222, 222, 255, 0.19);
    //  opacity: 0.5;
    transition: 0.3s;
    //box-shadow: 1.8px 3.7px 8px #767676;
  }
`;

const MainVideo = styled.video`
  width: 100vw;
  margin-top: -270px;
  z-index: 0;

`;
