import { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from "react-router-dom";
import axios from "axios";




//
export const FindIdPassword = () => {
    //탭 폼
    const [currentTab, setTab] = useState(0);

    const menuArr = [
        { name: '아이디 찾기', content: <AdminMemberList /> },
        { name: '비밀번호 찾기', content: <AdimWdList /> },
    ];
    const selectMenuHandler = (index: any) => {
        setTab(index);
    };





    //네비
    const navigate = useNavigate();

    //findIdForm
    const [findIdForm, setFindIdForm] = useState({
        memName: '',
        memEmail: ''
    });

    //findPasswordForm
    const [findPasswordForm, setFindPasswordForm] = useState({
        memId: '',
        memEmail: ''
    });

    //message
    const [message, setMessage] = useState('');

    //handleFindIdChange
    const handleFindIdChange = (e) => {
        setFindIdForm({ ...findIdForm, [e.target.name]: e.target.value });
    };

    //handleFindPasswordChange
    const handleFindPasswordChange = (e) => {
        setFindPasswordForm({ ...findPasswordForm, [e.target.name]: e.target.value });
    };

    const handleFindId = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('/auth/find-id', findIdForm);
            alert(`아이디: ${response.data}`);
        } catch (error) {
            alert('회원정보를 찾지 못했습니다.');
        }
    };

    //handleFindPassword - axios.post
    const handleFindPassword = async (e) => {
        e.preventDefault();
        try {
            const response
                = await axios.post('/auth/find-password', findPasswordForm);

            setMessage('비밀번호 재설정 이메일 발송완료');
            alert('비밀번호 재설정 이메일 발송완료');

        } catch (error) {
            setMessage('요청을 처리하는 동안 오류가 발생했습니다');
            alert('요청을 처리하는 동안 오류가 발생했습니다');
        }
    };



    //html -----------------------------------
    return (
        <div>
            <Header/>
            <S.Container>
                <EditMenu/>
                <S.Wrapper>
                    <S.TabMenu>
                        {menuArr.map((tap, index) => {
                            return (
                                <div
                                    key={index}
                                    className={currentTab === index ? 'submenu focused'
                                        : 'submenu'}
                                    onClick={() => selectMenuHandler(index)}
                                >
                                    {tap.name}
                                </div>
                            );
                        })}
                    </S.TabMenu>
                    <div>
                        <div>{menuArr[currentTab].content}</div>
                    </div>
                </S.Wrapper>
            </S.Container>
        </div>
    );
}
export default FindIdPassword;


const WrapBody = styled.div`
    //background-color: #fff;
    width: 100%;
    margin: 0;
    padding: 0;
    overflow-x: hidden;
    -webkit-box-sizing: border-box;
`;


// 전체 박스
const Wrap = styled.div`
    display: flex;
    justify-content: center;
    //max-width: 1200px;
    width: 1200px;
    margin: 0 auto;
    padding: 60px 40px;
    margin-top: 20px;
`;


//탭메뉴
const TabMenu = styled.ul`
    list-style: none;
    margin-bottom: 20px;
    margin-top: 10px;
    display: flex;
    flex-direction: row;
    align-items: center;


    //디자인- 비활성화
    color: #000;
    font-weight: 300;
    text-align: center;
    display: flex;
    justify-content: center;
    font-size: 16px;

    //탭메뉴

    .submenu {
        // 기본 Tabmenu 에 대한 CSS를 구현
        width: 300px;
        heigth: 30px;
        padding: 10px;
        transition: 0.5s;
        border: 1px solid rgba(255, 255, 255, 0.2);
        outline: none;
        cursor: pointer;
        color: #fff;
        font-weight: 300;
        text-align: center;
        display: flex;
        justify-content: center;
        font-size: 16px;
    }

    //활설화
    .focused {
        //선택된 Tabmenu 에만 적용되는 CSS를 구현
        background-color: #1351f9 ;
        //background-color: rgba(11, 11, 13);
        color: #fff;
        font-weight: 300;
        text-align: center;
        display: flex;
        justify-content: center;
        font-size: 16px;

        &:focus{
            display: flex;
            justify-content: center;
            width: 1200px;
            height: 400px;
            margin: 0 auto;
            background-color: red;
            margin-bottom: 20px;
            padding: 80px 40px;
        }
    }

    & div.desc {
       
    }
`;

const Div = styled.div`
    display: flex;
    justify-content: center;
    width: 1200px;
    height: 400px;
    margin: 0 auto;
    background-color: red;
    margin-bottom: 20px;
    padding: 80px 40px;
`;
