//다크모드 스타일 세팅

export const lightTheme ={
    bgColor: '#F8F7F4',
    textColor: '#31302E',
    borderColor: '1px solid #eaeaea',
};

export const darkTheme ={
    bgColor: '#1E1E22',
    textColor: '#ccc',
    borderColor: '1px solid #2c2d33',
};

export const Theme ={
    lightTheme,
    darkTheme
};


const theme = {
    mainBlue: "#02d6e8",
    // backgroundGrey: "#f5f5f5",
  
    // border color
    borderLine: "#02d6e8",
    inputGray: "#dbdbdb",
  
    // font color
    fontMainBlack: "#424242",
    fontNavBlack: "#474747",
    fontSubNavBlack: "#424242",
    fontFilterGray: "#757575",
    fontGray: "#757575",
    placeholderGrey: "#bdbdbd",
  };
  
  export default theme;
