        font-size: 1.2rem;
        color: rgb(254,107,12,1);
        font-weight: 700;
        line-height: 1.5;
        letter-spacing: -0.5px;
    }
`;



const MainTop4 = () => {
    return (
        <TopList4Container>
            <Top4TitleBox>

                <Top4Title>
                    회원님을 위한,&nbsp;<span className="SectionANav">추천 콘텐츠</span>
                </Top4Title>

            </Top4TitleBox>


            <TopListBox>
                {TOP_FOUR_LIST.map(toplist => {
                    return (
                        <Top4WholeContainer key={toplist.id}>
                            <TopListImgAndContext>
                                <TopListImg src={toplist.movieThumbnail} alt="movieposter" />
                                <Top4ContextContainer>
                                    <Top4TitleText>{toplist.movieTitle}</Top4TitleText>
                                    <Top4SubTitleText>
                                        &#10077; {toplist.movieSubTitle} &#10078;
                                    </Top4SubTitleText>
                                    <br />
                                    <Top4SubTitleText>
                                        - {toplist.movieContent} -
                                    </Top4SubTitleText>
                                </Top4ContextContainer>
