import React, {useState} from 'react';
import './css/FindIdPassword.css';
import styled from "styled-components";



function FindIdPassword() {

    //변수
    const [activeIndex, setActiveIndex] = useState(0);

    //핸들러
    const tabClickHandler = (index) => {
        setActiveIndex(index);
    };

    //탭
    const tabContArr = [{
        tabTitle: (<li className={activeIndex === 0 ? "is-active" : ""} onClick={() => tabClickHandler(0)}> 탭1 </li>),
        tabCont: (<div> 탭1 내용 </div>)
    }, {
        tabTitle: (<li className={activeIndex === 1 ? "is-active" : ""} onClick={() => tabClickHandler(1)}> 탭2 </li>),
        tabCont: (<div> 탭2 내용 </div>)
    }];


    //HTML
    return (
        <WrapBody>
                <ul className="tabs is-boxed">
                    {tabContArr.map((section, index) => {
                        return section.tabTitle
                    })}
                </ul>
                <div>
                    {tabContArr[activeIndex].tabCont}
                </div>
        </WrapBody>
    );
}
export default FindIdPassword;



// style --------------------------
const WrapBody = styled.div`
    background-color: red;
    width: 100%;
    height: 100vh;
    -webkit-box-sizing: border-box;
    display: flex;
    justify-content: center;
    margin: 0 auto;
    padding: 60px 40px;
    margin-top: 20px;
`;


// 전체 박스
const Wrap = styled.div`
    display: flex;
    justify-content: center;
    //max-width: 1200px;
    width: 1200px;
    margin: 0 auto;
    padding: 60px 40px;
    margin-top: 20px;
`;
