import React from 'react';
import {Link, NavLink, useLocation} from 'react-router-dom';
import styled from "styled-components";
import top from "./images/top.png";
import SideBar from "./SideBar";
import axios from "axios";
import edit from "./images/edit.svg";
import play from "./images/play.svg";
import './css/Tab.css';
import TabBar from "./Tab";
import { useState } from 'react';




//MyPage
const MyPage = ({ children }) => {


    const [activeTab, setActiveTab] = useState(children[0].props.label);

    const handleTabClick = (tabLabel) => {
        setActiveTab(tabLabel);
    };

    //
    const pathName = useLocation().pathname;

    //
    let body = {};

    //
    const onSubmit = async () => {
        try {
            const response = await axios.post("https://backend.alittlevanilla.kro.kr/member/signup", body, {
                headers: {"Content-Type": "application/json"},
            });
            console.log(response.data.code);
            if (response.data.code === 1000) {
                window.location.href = "/emailcheck";
            }
        } catch (e) {
            console.log(e);
        }
    };


    return (
        <>
        <SideBar />
        <Wrap>

            <TopContainer>
                <Section>
                    <ProfileBoxContainer>
                        <TopImg src={top}></TopImg>

                        <TitelBox>홍길동 님
                            <img
                                className="profile-img"
                                src={edit}/>
                        </TitelBox>

                        <FormBlockBody>
                            <InputTextSizeW>
                                <EmailsButton
                                    type="button"
                                    onClick={() => {
                                        onSubmit();
                                    }}
                                >
                                    프로필 전환
                                </EmailsButton>
                            </InputTextSizeW>
                        </FormBlockBody>
                    </ProfileBoxContainer>

                    <Titel>나의 이용권 &nbsp;&nbsp;&nbsp;&nbsp;|&nbsp;&nbsp;&nbsp;&nbsp;스탠다드 정기권
                        <img
                            className="play-img"
                            src={play}/>
                    </Titel>
                </Section>
            </TopContainer>

            {/*  탭메뉴 구성  */}


            <TabMenu>
                <div className="tabs">
                    {children.map((child) => (
                        <Tab
                            key={child.props.label}
                            label={child.props.label}
                            onClick={handleTabClick}
                            activeTab={activeTab}
                        />
                    ))}
                    <div className="tab-content">
                        {children.map((child) =>
                            child.props.label === activeTab ? child.props.children : null
                        )}
                    </div>
                </div>


                    {/**/}
                    <Tabs>
                        <div label="Tab 1">
                            <p>Content for Tab 1</p>
                        </div>
                        <div label="Tab 2">
                            <p>Content for Tab 2</p>
                        </div>
                        <div label="Tab 3">
                            <p>Content for Tab 3</p>
                        </div>
                    </Tabs>

            </TabMenu>
        </Wrap>
        </>
    );
}
export default MyPage;




