import axios from "axios";
import React, { useState } from "react";
import styled from "styled-components";


// Login-로그인
function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    let body = {
        username: email,
        password: password,
    };

    const onSubmit = async () => {
        try {
            const response = await axios.post(
                "https://backend.alittlevanilla.kro.kr/member/authenticate",
                body,
                {
                    headers: { "Content-Type": "application/json" },
                }
            );

            console.log(response.data.token);

            if (response.data.token !== null) {
                localStorage.clear();
                localStorage.setItem("token", response.data.token);
                console.log("response.data.token:  " + response.data.token);

                window.location.replace("/");
            } else {
                setEmail("");
                setPassword("");
                localStorage.clear();
            }
        } catch (e) {
            alert(
                "회원 정보가 일치 하지 않습니다. 아이디와 비민번호를 다시 확인해 주세요!"
            );
            console.log(e);
        }
    };
//html
    return (
        <>
            <LoginWrap>
                <LoginContainer>
                    <LoginHeadLogo>
                        <h1>
                            <a>{<IconLogo />}</a>
                        </h1>
                    </LoginHeadLogo>
                    <LoginHeadText>
                        <NeedLogin>
                            <BackgroundText>
                                <Text>로그인</Text>
                                <Background></Background>
                            </BackgroundText>
                            이 필요한 서비스 입니다.
                        </NeedLogin>
                        <CouponImg src="https://image.idus.com/static/signup/web_benefit_signup.png" />
                    </LoginHeadText>
                    <LoginSignupContent>
                        <BorderAndText>
                            <span>간편 로그인/회원가입</span>
                        </BorderAndText>

                        <VerticalButtons>
                            <BarButton
                                href="https://backend.alittlevanilla.kro.kr/oauth2/authorization/kakao"
                                className="kakao"
                            >
                                <SpIcon className="Kakaotalk"></SpIcon>
                                카카오로 3초만에 시작하기
                            </BarButton>
                            <BarButton className="email" href="/signupemail">
                                이메일로 가입하기
                            </BarButton>
                        </VerticalButtons>

                        <HorizontalButtons>
                            <RadiusButton
                                className="naver"
                                href="/w/naver/login?redirect_uri=https%3A%2F%2Fwww.idus.com%2Fw%2Fmain%2Ftoday-recommend-product"
                            >
                                <SpIcon className="naver"></SpIcon>
                            </RadiusButton>

                            <RadiusButton className="facebook" href="">
                                <SpIcon className="facebook"></SpIcon>
                            </RadiusButton>

                            <RadiusButton className="twitter" href="">
                                <SpIcon className="twitter"></SpIcon>
                            </RadiusButton>

                            <RadiusButton className="apple" href="">
                                <SpIcon className="apple"></SpIcon>
                            </RadiusButton>
                        </HorizontalButtons>
                    </LoginSignupContent>
                    <LoginSigninContent>
                        <BorderAndText>
                            <span>이메일 로그인</span>
                        </BorderAndText>
                        <EmailLoginContainer>
                            <div>
                                <EmailLoginInput
                                    id="email"
                                    type="email"
                                    value={email}
                                    placeholder="이메일"
                                    required
                                    onChange={(e) => {
                                        setEmail(e.target.value);
                                    }}
                                />
                                <EmailLoginInput
                                    id="password"
                                    // type="password"
                                    value={password}
                                    placeholder="비밀번호"
                                    required
                                    onChange={(e) => {
                                        setPassword(e.target.value);
                                    }}
                                />
                            </div>
                            <EmailLoginOption>
                                <div>
                                    <InputCheckbox>
                                        <Bp type="checkbox"></Bp>
                                    </InputCheckbox>
                                    <label>이메일 저장하기</label>
                                </div>
                                <a>아이디 / 비밀번호 찾기</a>
                            </EmailLoginOption>
                        </EmailLoginContainer>
                        <CommonButton
                            type="button"
                            onClick={() => {
                                onSubmit();
                                console.log("body: " + email + ", " + password);
                            }}
                        >
                            로그인
                        </CommonButton>
                    </LoginSigninContent>
                </LoginContainer>
            </LoginWrap>
        </>
    );
}

