import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";

// css
import "../../common/css/ChangePassword.css"
import styled from "styled-components";




function ChangePassword() {

    const navigate = useNavigate();

    const location = useLocation();

    const [isResetMode, setIsResetMode] = useState(false);

    const [passwords, setPasswords] = useState({
        memNewPassword: '',
        memNewPasswordCheck: ''
    });

    const [passwordError, setPasswordError] = useState('');

    // URL 확인 후 모드 변경
    useEffect(() => {
        const params = new URLSearchParams(location.search);
        const tokenParam = params.get('token');
        if (tokenParam) {
            setIsResetMode(true);
            sessionStorage.setItem('resetToken', tokenParam);
            navigate(location.pathname, { replace: true });
        }
    }, [location, navigate]);

    // 비밀번호 유효성 검사 조건
    const validatePassword = (password) => {
        const minLength = 8;
        const hasUpperCase = /[A-Z]/.test(password);
        const hasLowerCase = /[a-z]/.test(password);
        const hasNumbers = /\d/.test(password);
        const hasNonalphas = /\W/.test(password);

        if (password.length < minLength) {
            return '비밀번호는 최소 8자 이상이어야 합니다.';
        } else if (!(hasUpperCase && hasLowerCase && hasNumbers && hasNonalphas)) {
            return '비밀번호는 대문자, 소문자, 숫자, 특수문자를 모두 포함해야 합니다.';
        }
        return '';
    };

    // 필드 값 변경 될 시 호출
    const handleChange = (e) => {
        const { name, value } = e.target;
        setPasswords(prevState => ({
            ...prevState,
            [name]: value
        }));
        if (name === 'memNewPassword') {
            setPasswordError(validatePassword(value));
        }
    };

    // 폼 제출 시 호출
    const handleSubmit = async (e) => {
        e.preventDefault();

        // 비밀번호 검사
        if (passwordError) {
            alert(passwordError);
            return;
        }

        // 일치 여부 검사
        if (passwords.memNewPassword !== passwords.memNewPasswordCheck) {
            alert('새 비밀번호와 확인 비밀번호가 일치하지 않습니다.');
            return;
        }

        try {
            let response;
            const updatePasswordDto = {
                memNewPassword: passwords.memNewPassword,
                memNewPasswordCheck: passwords.memNewPasswordCheck
            };

            // 재설정 & 변경 모드 엔드포인트
            if (isResetMode) {
                const token = sessionStorage.getItem('resetToken');
                response = await axios.post('/auth/reset-password',
                    updatePasswordDto,
                    { params: { token } }
                );
                sessionStorage.removeItem('resetToken');
            } else {
                const accessToken = localStorage.getItem('accessToken');
                response = await axios.put('/auth/update', updatePasswordDto, {
                    headers: { 'Authorization': `Bearer ${accessToken}` }
                });
            }

            // 비밀번호 변경시 알림, 이동
            if (response.status === 200) {
                alert('비밀번호가 성공적으로 변경되었습니다.');
                navigate(isResetMode ? '/login' : -1);
            }
        } catch (error) {
            console.error('Error changing password:', error);
            alert(error.response?.data || '비밀번호를 재설정하는 동안 오류가 발생했습니다');
        }
    };


    return (
        <WrapLogin>
            <HeadBannerGroup />
            <ReauthPhone>
                <LoginWrap>
                    <LoginLogo>
                        <h1>
                            {/* <LogoA href="/">
                <SpIcon />
              </LogoA> */}
                        </h1>
                    </LoginLogo>

                    <LoginSection>
                        <div className="step-bar">
                            <span className="gradation-blue"></span>
                        </div>
                        <LoginTitle>회원가입
                            <h5>아이디와 이메일로 간편하게 무빙을 시작하세요!</h5>
                        </LoginTitle>


                        {/*<SignupStep className="wrap">*/}
                        {/*    <ul>*/}
                        {/*        <li>1</li>*/}
                        {/*        <IsActive>2</IsActive>*/}
                        {/*    </ul>*/}
                        {/*    <Title>가입 정보 입력하기</Title>*/}
                        {/*</SignupStep>*/}


                        {/*아이디*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 아이디
                            </FormBlockHead>
                            <FormBlockBody>
                                <InputTextSizeW>
                                    <EmailInput
                                        id="email"
                                        type="email"
                                        value={email}
                                        placeholder="아이디를 입력해주세요."
                                        required
                                        onChange={(e) => {
                                            setEmail(e.target.value);
                                        }}
                                        onBlur={() => emailCheck()}
                                    />
                                </InputTextSizeW>
                                <FormError>{checkedEmail}</FormError>
                            </FormBlockBody>
                        </FormBlock>


                        {/*비밀번호*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 비밀번호
                            </FormBlockHead>
                            <FormBlockBody>
                                <InputTextSizeW>
                                    <EmailInput
                                        id="password"
                                        // type="password"
                                        value={password}
                                        placeholder="비밀번호 (영문+숫자+특수문자 8자 이상)"
                                        required
                                        onChange={(e) => {
                                            setPassword(e.target.value);
                                        }}
                                    />
                                </InputTextSizeW>
                            </FormBlockBody>
                            <FormBlockBody>
                                <InputTextSizeW>
                                    <EmailInput placeholder="비밀번호 확인" required/>
                                </InputTextSizeW>
                            </FormBlockBody>
                        </FormBlock>


                        {/*이름*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 이름
                            </FormBlockHead>
                            <FormBlockBody>
                                <InputTextSizeWTypeL>
                                    <EmailInput
                                        id="name"
                                        value={nickname}
                                        type="text"
                                        placeholder="이름을 입력해 주세요"
                                        required
                                        onChange={(e) => {
                                            setNickname(e.target.value);
                                        }}
                                    />
                                </InputTextSizeWTypeL>
                            </FormBlockBody>
                        </FormBlock>


                        {/*전화번호*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 전화번호  (＊하이픈 제외 입력)
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <PhoneInput type="hidden" required/>
                                        <PhoneInput
                                            type="tel"
                                            placeholder="010-1234-5678"
                                            name="numberValue"
                                            value={numberValue || ''}
                                            onChange={handleNumber}
                                            required
                                        />
                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>


                        {/*★★★★여기부터!!!!!!!!!!!!!!!!!!!*/}
                        {/*이메일 인증*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 이메일
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailsInput type="hidden" required/>
                                        <EmailsInput
                                            type="email"
                                            placeholder="이메일을 입력해주세요"
                                            data-auth=""
                                            required
                                        />
                                        <EmailsButton
                                            type="button"
                                            onClick={() => {
                                                onSubmit();
                                            }}
                                        >
                                            이메일 전송
                                        </EmailsButton>

                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>


                        {/*이메일 인증*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 인증코드
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailsInput type="hidden" required/>
                                        <EmailsInput
                                            type="email"
                                            placeholder="이메일을 확인해주세요"
                                            data-auth=""
                                            required
                                        />
                                        <EmailsButton
                                            type="button"
                                            onClick={() => {
                                                onSubmit();
                                            }}
                                        >
                                            인증코드
                                        </EmailsButton>
                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>


                        {/*생년월일*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 생년월일
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailInput type="hidden" required/>
                                        <EmailInput
                                            type="date"
                                            data-auth=""
                                            required
                                        />
                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>



                        {/*성별*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 성별
                            </FormBlockHead>

                            <FormBlockBody>
                                {/*  성별 옵션 변경  */}
                                <CustomSelect name="Gender"
                                              value={gender}
                                              data-auth=""
                                              onChange={(e) => {
                                                  setGender(e.target.value);
                                              }}
                                              required>
                                    <CustomOption value="">성별</CustomOption>
                                    <CustomOption value="">남성</CustomOption>
                                    <CustomOption value="">여성</CustomOption>
                                    <CustomOption value="">선택안함</CustomOption>
                                </CustomSelect>
                            </FormBlockBody>
                        </FormBlock>



                        {/*모두 동의 합니다*/}
                        <FormBlockCheckAllWrap>
                            <Terms>
                                <TermsHead>
                                    <InputCheckBox>
                                        <input
                                            type="checkbox"
                                            value="agree"
                                            onChange={(e) => {
                                                allAgreeHandler(e.currentTarget.checked);
                                            }}
                                            checked={isAllChecked}
                                        />
                                    </InputCheckBox>
                                    <TermsLabel onClick={fake}>모두 동의합니다.</TermsLabel>
                                </TermsHead>

                                <TermsBody>
                                    <TermsItem>
                                        <InputCheckBox>
                                            {/* <Terms1 type="checkbox"></Terms1> */}
                                            <input
                                                type="checkbox"
                                                value="provision"
                                                onChange={(e) => {
                                                    agreeHandler(e.currentTarget.checked, e.target.value);
                                                }}
                                                checked={
                                                    checkedItems.includes("provision") ? true : false
                                                }
                                            />
                                        </InputCheckBox>
                                        <Terms1Label>만 14세 이상입니다.</Terms1Label>
                                    </TermsItem>

                                    {/*  */}

                                    <TermsItem>
                                        <InputCheckBox>
                                            {/* <Terms1 type="checkbox"></Terms1> */}
                                            <input
                                                type="checkbox"
                                                value="privacy"
                                                onChange={(e) => {
                                                    agreeHandler(e.currentTarget.checked, e.target.value);
                                                }}
                                                checked={
                                                    checkedItems.includes("privacy") ? true : false
                                                }
                                            />
                                        </InputCheckBox>
                                        <Terms2A onClick={fake}>이용약관 필수 동의</Terms2A>
                                    </TermsItem>
                                    {/*  */}
                                </TermsBody>
                            </Terms>
                            <Terms1Error/>
                            <TermsError/>
                        </FormBlockCheckAllWrap>



                        {/*회원가입 버튼*/}
                        <FormBlockSubmit>
                            <FormBlockBody>
                                <BtnLogin
                                    type="submit"
                                    onClick={() => {
                                        onSubmit();
                                    }}
                                >
                                    가입하기
                                </BtnLogin>
                            </FormBlockBody>
                        </FormBlockSubmit>


                        {/*회원가입 속 로그인하러 가기*/}
                        <AdditionTxt>
                            이미 가입하셨다면&nbsp;&nbsp;<a href="/login">로그인</a>
                        </AdditionTxt>

                    </LoginSection>
                </LoginWrap>
            </ReauthPhone>
        </WrapLogin>
);
}
export default ChangePassword;
