import React, {useState} from "react";
import {Link, useNavigate} from "react-router-dom";
import axios from 'axios';
// import './css/NoticeWrite.css';
import styled from "styled-components";



//공지사항 글쓰기
function NoticeWrite() {

    //변수
    const [boardTitle, setBoardTitle] = useState('');
    const [boardContent, setBoardContent] = useState('');
    const [fileInputs, setFileInputs] = useState([0]); // 파일 입력 상태
    const [files, setFiles] = useState([]);
    const navigate = useNavigate();

    //핸들러
    const handleTitleChange = (e) => {
        setBoardTitle(e.target.value);
    };

    const handleContentChange = (e) => {
        setBoardContent(e.target.value);
    };

    const handleFileChange = (index, e) => {
        const newFiles = [...files];
        newFiles[index] = e.target.files[0];
        setFiles(newFiles);
    };

    const addFileInput = () => {
        if (fileInputs.length >= 5) {
            alert("최대 5개의 파일만 업로드할 수 있습니다.");
            return;
        }
        setFileInputs([...fileInputs, fileInputs.length]);
    };

    const removeFileInput = (index) => {
        const newFileInputs = fileInputs.filter((_, i) => i !== index);
        const newFiles = files.filter((_, i) => i !== index);
        setFileInputs(newFileInputs);
        setFiles(newFiles);
    };


    //handleSubmit
    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = {
            boardTitle: boardTitle, boardContent: boardContent,
        };

        //비동기
        try {
            const response = await axios.post('/admin/boardWrite', data);
            console.log('게시글 작성 성공:', response);
            const boardId = response.data.boardId; // 서버에서 반환한 게시글 ID

            if (files.length > 0) {
                await uploadFiles(boardId, files);
            } else {
                navigate('/admin/Notice');
            }
        } catch (error) {
            console.error('게시글 작성 실패:', error);
            alert('게시글 작성에 실패했습니다.');
        }
    };


    //uploadFiles
    const uploadFiles = async (boardId, files) => {
        const formData = new FormData();
        files.forEach((file) => {
            if (file) {
                formData.append('files', file); // 'files'는 서버에서 기대하는 파라미터 이름
            }
        });

        try {
            await axios.post(`/admin/boardUpload/${boardId}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log('파일 업로드 성공');
            navigate('/admin/Notice');
        } catch (error) {
            console.error('파일 업로드 실패:', error.response ? error.response.data : error.message);
            alert('파일 업로드에 실패했습니다. 게시글은 작성되었습니다.');
            navigate('/admin/Notice');
        }
    };



    //HTML
    return(

        <Wrap>
            <FromNoticeWrap>
                <Header className="name">
                    <Link to={`/user/notice/`}>
                        게시판
                    </Link>
                </Header>

                {/*폼 기능*/}
                <FormWrite onSubmit={handleSubmit}>

                    <InputTextSizeWTypeL>
                        {/* 제목 폼 */}
                        <FormBlockHead>
                            <AsteriskRed></AsteriskRed>제목
                        </FormBlockHead>
                        <InputWrite
                            type="text"
                            id="title"
                            name="title"
                            required
                            placeholder="제목을 입력하세요"
                            value={boardTitle}
                            onChange={handleTitleChange}
                        />
                        <FormBlockHead>
                            <AsteriskRed></AsteriskRed>작성일
                        </FormBlockHead>
                    {/* 등록일 폼 */}
                    <InputDate
                        type="date"
                        id="date"
                        name="date"
                        required
                    />
                    </InputTextSizeWTypeL>


                    {/* 내용 */}
                    <FormBlockHeads>
                        <AsteriskReds></AsteriskReds>내용

                        {/* 내용 폼 */}
                        <TextWrite
                            name="content"
                            id="content"
                            cols="auto"
                            rows="auto"
                            required
                            placeholder="내용을 입력하세요"
                            value={boardContent}
                            onChange={handleContentChange}
                        ></TextWrite>
                    </FormBlockHeads>





                    {fileInputs.map((input, index) => (<div className="FileUpload" key={index}>
                            <input
                                type="file"
                                id={`file_${index}`}
                                name={`file_${index}`}
                                onChange={(e) => handleFileChange(index, e)}
                            />
                            <button type="button" onClick={() => removeFileInput(index)}>삭제</button>
                        </div>))}


                    {/* 추가 파일 업로드 버튼 */}
                    {/*<div className="AddFileUpload">*/}
                    {/*    <button type="button" onClick={addFileInput}>파일 추가</button>*/}
                    {/*</div>*/}


                    {/* 글쓰기 버튼 */}
                    {/*<div className="WriteSubmit">*/}
                    {/*    <input type="submit" value="글쓰기" className="submit"/>*/}
                    {/*</div>*/}

                </FormWrite>
            </FromNoticeWrap>
       </Wrap>
    );
}
export default NoticeWrite;


// style
//전체
const Wrap = styled.div`
    width: 100%;
    //height: 100vh;
    position: relative;
    margin: 0 auto;
    //padding: 40px 40px;
    //background: rgb(255,255,255);
    background: #fff;
    display: inline-block;
`;
//감싸는 전체
const FromNoticeWrap = styled.div`
    width: 1024px;
    position: relative;
    margin: 0 auto;
    background: #fff;
    margin-bottom: 48px;
    padding-top: 40px;
`;
//제목감싸는 박스
const Header = styled.div`
    width: 1024px;
    margin: 0 auto;
    //background: red;
    font-family: 'SUIT-Regular' !important;
    color: rgb(51, 61, 75);
    font-size: 36px;
    font-weight: 800;
    text-align: left;
`;
//제목+ 등록 폼 감싸는 박스
const InputTextSizeWTypeL = styled.div`
  box-sizing: border-box;
  vertical-align: middle;
  height: 48px;
  display: flex;
  width: 1024px;
  margin-top: 1px;
  text-align: left;
`;
//폼 시작 전 글씨 스타일
// 모든 폼 폰트 사이즈
const FormBlockHead = styled.h3`
    font-size: 16px;
    color: #0f2027;
    line-height: 40px;
    //text-indent: 5px;
    font-family: 'SUIT-Regular' !important;
    font-weight: 200;
    //text-indent: px;
    margin-right: 10px;
    text-align: left;
`;
const AsteriskRed = styled.em`
  color: #ff27a3;
  font-size: 12px;
  display: inline-block;
`;

//등록폼
const InputDate = styled.input`
    width: 200px;
    height: 40px;
    //margin-left: 40px;
    border: none;
    outline: none;
    display: flex;
    justify-content: right;
    float: right;
    /*폰트 디자인*/
    padding: 15px 5px;
    text-indent: 10px;
    font-size: 13px;
    font-weight: 500;
    background-color: #fff;
    -webkit-font-smoothing: antialiased;
    text-align: left;

    /*폼 디자인*/
    border: 1px solid #ccc;
    //margin-left: 20px;
    margin-right: auto;
`;

//제목 폼
const InputWrite = styled.input`
    /*전체 구도*/
    width: 710px;
    height: 40px;
    //margin: 0 auto;
    border: none;
    outline: none;
    display: flex;
    justify-content: left;
    margin-right: 20px;
    
    /*폰트 디자인*/
    padding: 15px 5px;
    text-indent: 10px;
    font-size: 13px;
    font-weight: 500;
    background-color: #fff;
    -webkit-font-smoothing: antialiased;
    text-align: left;

    /*폼 디자인*/
    border: 1px solid #ccc;
`;

//내용 폼 글씨
const FormBlockHeads = styled.h3`
    width: 1024px;
    font-size: 16px;
    color: #0f2027;
    line-height: 50px;
    //text-indent: 5px;
    font-family: 'SUIT-Regular' !important;
    font-weight: 200;
    //text-indent: px;
    
    text-align: left;
`;
const AsteriskReds = styled.em`
  color: #ff27a3;
  font-size: 12px;
  display: inline-block;
`;


//내용 폼
const TextWrite = styled.textarea`
    float: right;
    width: 950px;
    height: 500px;
    resize: none;
    border: none;
    outline: none;
    border: 1px solid #ccc;
    margin-top: 20px;
    
    /*폰트 디자인*/
    text-indent: 10px;
    font-size: 13px;
    font-weight: 500;
    background-color: #fff;
    -webkit-font-smoothing: antialiased;
    text-align: left;
    padding: 15px 5px;
`;



const FormWrite = styled.form`
    width: 1024px;
    /* border: 1px solid red; */
    display: flex;
    flex-direction: column;
`;
