import React, {useState} from "react";
import {Link, useNavigate} from "react-router-dom";
import axios from 'axios';
// import './css/NoticeWrite.css';
import styled from "styled-components";


//공지사항 글쓰기
function NoticeWrite() {

    //변수
    const [boardTitle, setBoardTitle] = useState('');
    const [boardContent, setBoardContent] = useState('');
    const [fileInputs, setFileInputs] = useState([0]); // 파일 입력 상태
    const [files, setFiles] = useState([]);
    const navigate = useNavigate();

    //핸들러
    const handleTitleChange = (e) => {
        setBoardTitle(e.target.value);
    };

    const handleContentChange = (e) => {
        setBoardContent(e.target.value);
    };

    const handleFileChange = (index, e) => {
        const newFiles = [...files];
        newFiles[index] = e.target.files[0];
        setFiles(newFiles);
    };

    const addFileInput = () => {
        if (fileInputs.length >= 5) {
            alert("최대 5개의 파일만 업로드할 수 있습니다.");
            return;
        }
        setFileInputs([...fileInputs, fileInputs.length]);
    };

    const removeFileInput = (index) => {
        const newFileInputs = fileInputs.filter((_, i) => i !== index);
        const newFiles = files.filter((_, i) => i !== index);
        setFileInputs(newFileInputs);
        setFiles(newFiles);
    };


    //handleSubmit
    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = {
            boardTitle: boardTitle, boardContent: boardContent,
        };

        //비동기
        try {
            const response = await axios.post('/admin/boardWrite', data);
            console.log('게시글 작성 성공:', response);
            const boardId = response.data.boardId; // 서버에서 반환한 게시글 ID

            if (files.length > 0) {
                await uploadFiles(boardId, files);
            } else {
                navigate('/admin/Notice');
            }
        } catch (error) {
            console.error('게시글 작성 실패:', error);
            alert('게시글 작성에 실패했습니다.');
        }
    };


    //uploadFiles
    const uploadFiles = async (boardId, files) => {
        const formData = new FormData();
        files.forEach((file) => {
            if (file) {
                formData.append('files', file); // 'files'는 서버에서 기대하는 파라미터 이름
            }
        });

        try {
            await axios.post(`/admin/boardUpload/${boardId}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log('파일 업로드 성공');
            navigate('/admin/Notice');
        } catch (error) {
            console.error('파일 업로드 실패:', error.response ? error.response.data : error.message);
            alert('파일 업로드에 실패했습니다. 게시글은 작성되었습니다.');
            navigate('/admin/Notice');
        }
    };


    //HTML
    return (

        <Wrap>
            <FromNoticeWrap>
                <Header className="name">
                    <Link to={`/user/notice/`}>
                        게시판
                    </Link>
                </Header>

                {/*폼 기능*/}
                <FormWrite onSubmit={handleSubmit}>

                    <InputTextSizeWTypeL>
                        {/* 제목 폼 */}
                        <FormBlockHead>
                            <AsteriskRed></AsteriskRed>제목
                        </FormBlockHead>
                        <InputWrite
                            type="text"
                            id="title"
                            name="title"
                            required
                            placeholder="제목을 입력하세요"
                            value={boardTitle}
                            onChange={handleTitleChange}
                        />
                        <FormBlockHead>
                            <AsteriskRed></AsteriskRed>작성일
                        </FormBlockHead>
                        {/* 등록일 폼 */}
                        <InputDate
                            type="date"
                            id="date"
                            name="date"
                            required
                        />
                    </InputTextSizeWTypeL>


                    <FormBlock>
                        {/* 내용 */}
                        <FormBlockHead>
                            <AsteriskRed></AsteriskRed>내용
                        </FormBlockHead>

                            {/* 내용 폼 */}
                            <TextWrite
                                name="content"
                                id="content"
                                cols="auto"
                                rows="auto"
                                required
                                placeholder="내용을 입력하세요"
                                value={boardContent}
                                onChange={handleContentChange}
                            ></TextWrite>

                    </FormBlock>



                    <FormBlockFiles>
                            {fileInputs.map((input, index) => (

                                <div className="filebox" key={index}>

                                    FileUpload
                                    {/*<div className="FileUpload" key={index}>*/}
                                    {/*<div class="filebox">*/}
                                    {/*    <label for="ex_file">업로드</label>*/}
                                    {/*    <input type="file" id="ex_file">*/}
                                    {/*</div>*/}
                                    {/*className="filebox"*/}

                                {/* 파일 선택 */}

                                        <label
                                            for="ex_file">파일</label>

                                        <input type="file"
                                               id={`file_${index}`}
                                               name={`file_${index}`}
                                               onChange={(e) => handleFileChange(index, e)}
                                        />





                                {/*<button*/}
                                {/*    className="removeFile"*/}
                                {/*    type="button"*/}
                                {/*    onClick={() => removeFileInput(index)}>삭제*/}
                                {/*</button>*/}

                                </div>))}
                    </FormBlockFiles>

                                {/*<button*/}
                                {/*    className="AddFileUpload"*/}
                                {/*    type="button"*/}
                                {/*        onClick={addFileInput}>파일 추가</button>*/}



                            {/*/!* 글쓰기 버튼 *!/*/}
                            {/*<div className="WriteSubmit">*/}
                            {/*    <input type="submit" value="글쓰기" className="submit"/>*/}
                            {/*</div>*/}

                </FormWrite>
            </FromNoticeWrap>
        </Wrap>
);
}
export default NoticeWrite;


// style

//파일 전체 버튼
const InputFile = styled.input`
    width: 90px;
    height: 45px;
    border: 1px solid #cccccc;
    
    font-size: 14px;
    color: #0f2027;
    //padding: 10px 25px;
    text-align: center;
    display: flex;
    justify-content: center;
    cursor: pointer;
`;
    
    
const FormBlockFiles = styled.div`
    //사이즈
    width: 1024px;
    height: 45px;
    
    //여백
    margin-bottom: 48px;
    margin-left: 40px;
    margin-top: 40px;

    //구도
    display: flex;


    .filebox label {     /* 파일 필드*/
        display: inline-block;
        padding: .5em .75em;
        color: #0f2027;
        font-size: 14px;
        line-height: 30px;
        text-align: center;
        vertical-align: middle;
        background-color: #fdfdfd;
        cursor: pointer;
        border: 1px solid #ebebeb;
    ;
        
        
    //사이즈
        width: 90px;
        height: 45px;
        border: 1px solid #ccc;
        border-radius: 2px;
        background-color: #fff;
        
    }
    .filebox input[type="file"] {  /* 파일 필드 숨기기 */
        position: absolute;
        width: 1px;
        height: 1px;
        padding: 0;
        margin: -1px;
        overflow: hidden;
        clip:rect(0,0,0,0);
        border: 0;
    }
`;






//전체
const Wrap = styled.div`
width: 100%;
//height: 100vh;
position: relative;
margin: 0 auto;
//padding: 40px 40px;
//background: rgb(255,255,255);
background: #fff;
display: inline-block;
`;
//감싸는 전체
const FromNoticeWrap = styled.div`
width: 1024px;
position: relative;
margin: 0 auto;
background: #fff;
margin-bottom: 48px;
padding-top: 40px;
`;
//제목감싸는 박스
const Header = styled.div`
width: 1024px;
margin: 0 auto;
//background: red;
font-family: 'SUIT-Regular' !important;
color: rgb(51, 61, 75);
font-size: 36px;
font-weight: 800;
text-align: left;
`;
//제목+ 등록 폼 감싸는 박스
const InputTextSizeWTypeL = styled.div`
box-sizing: border-box;
