import axios from "axios";
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {Button, FormSelect} from "react-bootstrap";
import SelectorEngine from "bootstrap/js/src/dom/selector-engine";

import SideBar from "./SideBar";



// 회원정보설정
function MemberInfo() {

    const [values, setValues] = useState({
        nameValue: '',
        numberValue: '',
        contentValue: '',
    });

    const { nameValue, numberValue, contentValue } = values;

    const handleNumber = e => {
        const { value, name } = e.target;

        setValues({
            ...values,
            [name]: value,
        });
    };

    useEffect(() => {
        if (numberValue.length === 11) {
            setValues({
                numberValue: numberValue.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3'),
            });
        } else if (numberValue.length === 13) {
            setValues({
                numberValue: numberValue
                    //하이픈이 입력되면 공백으로 변경되고 하이픈이 다시 생성됨
                    .replace(/-/g, '')
                    .replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3'),
            });
        }
    }, [numberValue]);




    const [isAllChecked, setIsAllChecked] = useState(false);
    const [checkedItems, setCheckedItems] = useState([]);

    const allAgreeHandler = (checked) => {
        setIsAllChecked(!isAllChecked);
        if (checked) {
            setCheckedItems([...checkedItems, "provision", "privacy"]);
        } else if (
            (!checked && checkedItems.includes("provision")) ||
            (!checked && checkedItems.includes("privacy"))
        ) {
            setCheckedItems([]);
        }
    };

    const agreeHandler = (checked, value) => {
        if (checked) {
            setCheckedItems([...checkedItems, value]);
        } else if (!checked && checkedItems.includes(value)) {
            setCheckedItems(checkedItems.filter((el) => el !== value));
        }
    };


    //알림창
    const fake = () => {
        alert("현재 개인정보를 수집하고 있지 않습니다🙂 안심하고 테스트 해보세요.");
    };

    useEffect(() => {
        if (checkedItems.length >= 2) {
            setIsAllChecked(true);
        } else {
            setIsAllChecked(false);
        }
    }, [checkedItems]);


    //변수 설정-이메일/패스워드/닉네임
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [nickname, setNickname] = useState("");
    const [gender, setGender] = useState("");


    let body = {
        email: email,
        password: password,
        nickname: nickname,
        gender: gender,
        values:values,
    };

    //
    const onSubmit = async () => {
        try {
            const response = await axios.post(
                "https://backend.alittlevanilla.kro.kr/member/signup",
                body,
                {
                    headers: { "Content-Type": "application/json" },
                }
            );
            console.log(response.data.code);
            if (response.data.code === 1000) {
                window.location.href = "/emailcheck";
            }
        } catch (e) {
            console.log(e);
        }
    };

    // const onEmailAuth = async () => {
    //   console.log("onEmailAuth!");

    //   try {
    //     const response = await axios.post("https://backend.alittlevanilla.kro.kr/member/", body, {
    //       headers: { "Content-Type": "application/json" },
    //     });
    //   } catch (e) {
    //     console.log(e);
    //   }
    // };


    //이메일
    const emailCheck = async () => {
        if (email === "") {
            setCheckedEmail("필수 항목입니다.");
        } else {
            var regex =
                /^[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*\.[a-zA-Z]{2,3}$/i;
            console.log("비밀번호 유효성 검사 :: ", regex.test(email));



            if (regex.test(email)) {
                try {
                    const response = await axios.get(
                        "https://backend.alittlevanilla.kro.kr/member/" + email
                    );
                    if (response.data === true) {
                        setCheckedEmail("이미 존재하는 이메일 입니다.");
                    } else {
                        setCheckedEmail("가능");
                        console.log("가능");
                    }
                } catch (e) {
                    console.log(e);
                }
            } else {
                setCheckedEmail("아이디를 확인해 주세요.");
            }
        }
    };

    const [checkedEmail, setCheckedEmail] = useState("");

    return (
        <>
        <WrapLogin>
            <HeadBannerGroup />
            <ReauthPhone>
                <LoginWrap>
                    <LoginLogo>
                        <h1>
                            {/* <LogoA href="/">
                <SpIcon />
              </LogoA> */}
                        </h1>
                    </LoginLogo>

                    <LoginSection>
                        {/*<div className="step-bar">*/}
                        {/*    <span className="gradation-blue"></span>*/}
                        {/*</div>*/}
                        <LoginTitle>회원정보 수정
                            <h5>고객님의 회원정보는 사이트에서 통합 관리하고 있습니다.</h5>
                        </LoginTitle>


                        {/*이름*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 이름
                            </FormBlockHead>
                            <FormBlockBody>
                                <InputTextSizeWTypeL>
                                    <EmailInput
                                        id="name"
                                        value={nickname}
                                        type="disabled"
                                        disabled="disabled"
                                        required
                                    />
                                </InputTextSizeWTypeL>
                            </FormBlockBody>
                        </FormBlock>


                        {/*아이디*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 아이디
                            </FormBlockHead>
                            <FormBlockBody>
                                <InputTextSizeW>
                                    <EmailInput
                                        id="email"
                                        type="disabled"
                                        value={email}
                                        disabled="disabled"
                                        required
                                        onChange={(e) => {
                                            setEmail(e.target.value);
                                        }}
                                        onBlur={() => emailCheck()}
                                    />
                                </InputTextSizeW>
                                <FormError>{checkedEmail}</FormError>
                            </FormBlockBody>
                        </FormBlock>


                        {/*비밀번호*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 비밀번호 변경
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailsInput type="hidden" required/>
                                        <EmailsInput
                                            type="password"
                                            value={password}
                                            placeholder="비밀번호 변경 (영문+숫자+특수문자 8자 이상)"
                                            required
                                            onChange={(e) => {
                                                setPassword(e.target.value);
                                            }}
                                        />
                                        <EmailsButton
                                            type="button"
                                            onClick={() => {
                                                onSubmit();
                                            }}
                                        >
                                           비밀번호 변경
                                        </EmailsButton>

                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>





                        {/*전화번호*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 전화번호 변경
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <PhoneInput type="hidden" required/>
                                        <PhoneInput
                                            type="tel"
                                            placeholder="010-1234-5678"
                                            name="numberValue"
                                            value={numberValue || ''}
                                            onChange={handleNumber}
                                            required
                                        />
                                        <EmailsButton
                                            type="button"
                                            onClick={() => {
                                                onSubmit();
                                            }}
                                        >
                                            전화번호 변경
                                        </EmailsButton>
                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>



                        {/*이메일 인증*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 이메일
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailsInput type="hidden" required/>
                                        <EmailsInput
                                            type="email"
                                            placeholder="수정된 이메일을 입력해주세요."
                                            data-auth=""
                                            required
                                        />
                                        <EmailsButton
                                            type="button"
                                            onClick={() => {
                                                onSubmit();
                                            }}
                                        >
                                            이메일 변경
                                        </EmailsButton>

                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>




                        {/*생년월일*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 생년월일
                            </FormBlockHead>
                            <FormBlockBody>
                                <UiInputBtnCombo>
                                    <InputTextSizeWTypeL>
                                        <EmailInput type="hidden" disabled />
                                        <EmailInput
                                            readOnly
                                            type="text"
                                            name="val"
                                            disabled
                                            required
                                        />
                                    </InputTextSizeWTypeL>
                                </UiInputBtnCombo>
                            </FormBlockBody>
                        </FormBlock>



                        {/*성별*/}
                        <FormBlock>
                            <FormBlockHead>
                                <AsteriskRed>*</AsteriskRed> 성별
                            </FormBlockHead>

                            <FormBlockBody>
                                {/*  성별 옵션 변경  */}
                                <CustomSelect name="Gender"
                                              value={gender}
                                              disabled="disabled"
                                              required>
                                </CustomSelect>
                            </FormBlockBody>
                        </FormBlock>



                        {/*회원가입 버튼*/}
                        <FormBlockSubmit>
                            <FormBlockBody>
                                <BtnLogin
                                    type="submit"
                                    onClick={() => {
                                        onSubmit();
                                    }}
                                >
                                    개인정보 수정
                                </BtnLogin>
                            </FormBlockBody>
                        </FormBlockSubmit>

                        
                    </LoginSection>
                </LoginWrap>
            </ReauthPhone>
        </WrapLogin>

    </>
    );
}

