import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CommonTable from '../../components/table/CommonTable';
import CommonTableColumn from '../../components/table/CommonTableColumn';
import CommonTableRow from '../../components/table/CommonTableRow';
import { postList } from '../../Data';


import styled from "styled-components";
import Pagination from "react-js-pagination";
// import './Paging.css';



//공지사항(게시판) 리스트
const PostList = ({ postsPerPage, totalPosts, paginate})  => {
    //페이지네이션
    const [page, setPage] = useState(1);

    const handlePageChange = (page) => {
        setPage(page);
    };

    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
        pageNumbers.push(i);
    }



    const [ dataList, setDataList ] = useState([]);

    //
    useEffect(() => {
        setDataList(postList);
    }, [ ])

    return (
            <Wrap>
                {/*<div align="center" className="name">*/}
                {/*    <h2 className="name-notice">공지사항</h2>*/}
                {/*</div>*/}
                <div className="step-bar">
                    <span className="gradation-blue"></span>
                </div>
                <CommonTable headersName={['글번호', '제목', '등록일', '조회수']}>
                    {
                        dataList ? dataList.map((item, index) => {
                            return (
                                <CommonTableRow key={index}>
                                    <CommonTableColumn>{item.no}</CommonTableColumn>
                                    <CommonTableColumn>
                                        <Link to={`/postView/${item.no}`}>{item.title}</Link>
                                    </CommonTableColumn>
                                    <CommonTableColumn>{item.createDate}</CommonTableColumn>
                                    <CommonTableColumn>{item.readCount}</CommonTableColumn>
                                </CommonTableRow>
                            )
                        }) : ''
                    }
                </CommonTable>


                {/*<Pagination*/}
                {/*    className="pagination"*/}
                {/*    activePage={page} // 현재 페이지*/}
                {/*    itemsCountPerPage={10} // 한 페이지랑 보여줄 아이템 갯수*/}
                {/*    totalItemsCount={10} // 총 아이템 갯수*/}
                {/*    pageRangeDisplayed={10} // paginator의 페이지 범위*/}
                {/*    prevPageText={"‹"} // "이전"을 나타낼 텍스트*/}
                {/*    nextPageText={"›"} // "다음"을 나타낼 텍스트*/}
                {/*    onChange={handlePageChange} // 페이지 변경을 핸들링하는 함수*/}
                {/*/>*/}


                <PageUl className="pagination">
                    {pageNumbers.map((number) => (
                        <PageLi key={number} className="page-item">
                            <PageSpan onClick={() => paginate(number)} className="page-link">
                                {number}
                            </PageSpan>
                        </PageLi>
                    ))}
                </PageUl>

            </Wrap>
    );
};
export default PostList;



const PageUl = styled.ul`
    display: flex;
    justify-content: center;

    /*background-color: red;*/
    width: 100%;
    margin: 40px auto;
    box-sizing: border-box;
    
    //float: left;
    //list-style: none;
    //text-align: center;
    //border-radius: 3px;
    //color: white;
    //padding: 1px;
    //border-top: 3px solid #186ead;
    //border-bottom: 3px solid #186ead;
    //background-color: rgba(0, 0, 0, 0.4);
`;

const PageLi = styled.li`
  display: inline-block;
  font-size: 17px;
  font-weight: 600;
  padding: 5px;
  border-radius: 5px;
  width: 25px;
  &:hover {
    cursor: pointer;
    color: white;
    background-color: #263a6c;
  }
  &:focus::after {
    color: white;
    background-color: #263a6c;
  }
`;

const PageSpan = styled.span`
  &:hover::after,
  &:focus::after {
    border-radius: 100%;
    color: white;
    background-color: #263a6c;
  }
`;


    const Wrap = styled.div`
    width: 100%;
    height: 100vh;
    position: relative;
    margin: 0 auto;
    //padding: 40px 40px;
    //background: rgb(255,255,255);
    background:#fff;
    display: inline-block;
 `;


//푸터 전체 - 위치/배경
const FooterTag = styled.footer`
  color: #6b7684;
  display: flex;
  align-items: center;
  flex-direction: column;
  margin-left: auto;
  margin-right: auto;
  padding: 26px 0;
  text-align: center;
  bottom: 0;
  width: 100%;
  z-index: 9999999!important;;
  background-color: rgb(11, 11, 13) !important;
  border-top: 1px solid rgb(25, 31, 40);
`;
