import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styled, { css } from "styled-components";
// import jwt_decode from "jwt-decode";
// import asasasa from "../Nav/asasasa.png";
// import QR from "../../Images/QR.png";


function Header() {
    const [isAppInstallHover, setIsAppInstallHover] = useState(false);
    const [isCallCenterHover, setIsCallCenterHover] = useState(false);
    const [isMyInfoHover, setIsMyInfoHover] = useState(false);
    const [isCategoryHover, setIsCategoryHover] = useState(false);
    const [isTopHover, setIsTopHover] = useState(false);

    const [email, setEmail] = useState("");

    const [cartnum, setCartnum] = useState("");

    const [word, setWord] = useState("");
    const issueKeyword = "어버이날";

    const [loginCheck, setLoginCheck] = useState("");
    const goMypage = () => {
        try {
            if (localStorage.getItem("token") === null) {
                alert("로그인 해주세요.");
                document.location.href = "/login";
            } else {
                document.location.href = "/mypage";
            }
        } catch (e) {
            console.log(e);
        }
    };
    const logout = () => {
        try {
            localStorage.clear();
            document.location.href = "/";
        } catch (e) {
            console.log(e);
        }
    };

    // async function fetchData() {
    //   const result = await axios.get("https://backend.alittlevanilla.kro.kr/cart/list", {
    //     headers: {
    //       Authorization: "Bearer " + localStorage.getItem("token"),
    //       "Content-Type": "application/json",
    //     },
    //   });
    //   console.log(JSON.stringify(result.data.result));

    //   setCartnum(result.data.result);
    // }

    useEffect(() => {
        try {
            if (localStorage.getItem("token") !== null) {
                setLoginCheck(true);
                // console.log("로그인: " + loginCheck);
                // console.log("token: " + localStorage.getItem("token"));
            } else {
                setLoginCheck(false);
                // fetchData();
            }
        } catch (error) {
            console.log("error: " + JSON.stringify(localStorage));
        }
    }, [loginCheck]);

    const onSubmit = async () => {
        if (word === "") {
            window.location.href = "/search/" + issueKeyword;
        } else {
            window.location.href = "/search/" + word;
        }
    };

    return (
        <HeaderTag>
            <TopNavigation>
                <InnerW>
                    <NavBtnUiDropdown>
                        <BtnDropdownReset
                            onMouseOver={() => setIsAppInstallHover(true)}
                            onMouseOut={() => setIsAppInstallHover(false)}
                            readOnly
                            value="어리틀바닐라 앱 설치하기"
                        />
                        <MenuDropdownAppInstallGuide
                            onMouseOver={() => setIsAppInstallHover(true)}
                            onMouseOut={() => setIsAppInstallHover(false)}
                            isAppInstallHover={isAppInstallHover}
                        >
                            <MenuDropdownAppH1>
                                <SpIconImgAppicon />
                                어리틀바닐라를 스마트폰으로 더욱 편리하게 사용하세요.
                            </MenuDropdownAppH1>
                            <MenuDropdownAppOl>
                                <MenuDropdownAppLi>
                                    <MenuDropdownAppH2>
                                        1. 문자로 앱설치 안내받기
                                    </MenuDropdownAppH2>
                                    <MenuDropdownAppForm>
                                        <InputText>
                                            <InputCellPhone />
                                        </InputText>
                                        <BtnPoint>전송</BtnPoint>
                                    </MenuDropdownAppForm>
                                    <MenuDropdownAppSpan>
                                        앱 설치 URL 전송을 위하여 휴대폰번호를 입력하며 입력된
                                        정보는 저장되지 않습니다.
                                    </MenuDropdownAppSpan>
                                </MenuDropdownAppLi>

                                <MenuDropdownAppLiTwice>
                                    <MenuDropdownAppH2>2. QR코드로 앱설치하기</MenuDropdownAppH2>
                                    <QrImgicon></QrImgicon>
                                </MenuDropdownAppLiTwice>
                            </MenuDropdownAppOl>
                        </MenuDropdownAppInstallGuide>
                    </NavBtnUiDropdown>

                    {/* 마이 페이지 */}
                    <Fr>
                        {loginCheck ? (
                            <>
                                <NavBtnUiDropdown>
                                    <BtnDropdown
                                        onMouseOver={() => setIsMyInfoHover(true)}
                                        onMouseOut={() => setIsMyInfoHover(false)}
                                    >
                                        {jwt_decode(localStorage.getItem("token")).nickname}님
                                    </BtnDropdown>
                                    <MyDropdown
                                        onMouseOver={() => setIsMyInfoHover(true)}
                                        onMouseOut={() => setIsMyInfoHover(false)}
                                        isMyInfoHover={isMyInfoHover}
                                    >
                                        <CustomerLi>
                                            <CustomerA
                                                onMouseOver={() => setIsTopHover(true)}
                                                onMouseOut={() => setIsTopHover(false)}
                                                isTopHover={isTopHover}
                                            >
                                                주문배송
                                            </CustomerA>
                                        </CustomerLi>
                                        <CustomerLi>
                                            <CustomerA
                                                onMouseOver={() => setIsTopHover(true)}
                                                onMouseOut={() => setIsTopHover(false)}
                                                isTopHover={isTopHover}
                                            >
                                                관심리스트
                                            </CustomerA>
                                        </CustomerLi>
                                        <CustomerLi>
                                            <CustomerA>쿠폰함</CustomerA>
                                        </CustomerLi>
                                        <CustomerLi>
                                            <CustomerA href="/mypage">회원 정보관리</CustomerA>
                                        </CustomerLi>
                                        <AddBorder
                                            onClick={() => {
                                                logout();
                                                console.log("로그아웃! body: " + email);
                                            }}
                                        >
                                            로그아웃
                                        </AddBorder>
                                    </MyDropdown>
                                </NavBtnUiDropdown>

                                {/* <GnbLoginBtn href="/signup">알림</GnbLoginBtn>
                <GnbLoginBtn href="/signup">메시지</GnbLoginBtn> */}
                                <NavBtnUiDropdown>
                                    <BtnDropdown
                                        onMouseOver={() => setIsCallCenterHover(true)}
                                        onMouseOut={() => setIsCallCenterHover(false)}
                                    >
                                        고객센터
                                    </BtnDropdown>
                                    <MenuDropdown
                                        onMouseOver={() => setIsCallCenterHover(true)}
                                        onMouseOut={() => setIsCallCenterHover(false)}
                                        isCallCenterHover={isCallCenterHover}
                                    >
                                        <CustomerLi>
                                            <CustomerA href="https://blog.naver.com/ghdalswl77">
                                                공지사항
                                            </CustomerA>
                                        </CustomerLi>
                                        {/* <CustomerLi>
                      <CustomerA>자주 묻는 질문</CustomerA>
                    </CustomerLi>
                    <CustomerLi>
                      <CustomerA>메일로 문의</CustomerA>
                    </CustomerLi> */}
                                    </MenuDropdown>
                                </NavBtnUiDropdown>
                            </>
                        ) : (
                            <>
                                <GnbLoginBtn href="/signup">회원가입</GnbLoginBtn>
                                <GnbLoginBtn href="/login">로그인</GnbLoginBtn>

                                <NavBtnUiDropdown>
                                    <BtnDropdown
                                        onMouseOver={() => setIsCallCenterHover(true)}
                                        onMouseOut={() => setIsCallCenterHover(false)}
                                        isCallCenterHover={isCallCenterHover}
                                    >
                                        고객센터
                                    </BtnDropdown>
                                    <MenuDropdown
                                        onMouseOver={() => setIsCallCenterHover(true)}
                                        onMouseOut={() => setIsCallCenterHover(false)}
                                        isCallCenterHover={isCallCenterHover}
                                    >
                                        <CustomerLi>
                                            <CustomerA href="https://github.com/minjipi/little_vanilla_front">
                                                공지사항
                                            </CustomerA>
                                        </CustomerLi>
                                        <CustomerLi>
                                            <CustomerA href="https://github.com/minjipi/little_vanilla_front/issues">
                                                자주 묻는 질문
                                            </CustomerA>
                                        </CustomerLi>
                                        <CustomerLi>
                                            <CustomerA href="https://blog.naver.com/ghdalswl77">
                                                블로그
                                            </CustomerA>
                                        </CustomerLi>
                                    </MenuDropdown>
                                </NavBtnUiDropdown>
                            </>
                        )}
                    </Fr>

                </InnerW>
            </TopNavigation>
            <SearchHeaderDesktop>
                <SearchHeaderDesktopBar>
                    <SearchHeaderDesktopLogo>
                        <IconIduslogo to="/">
                            <Iduslogo src={require("./logo.png")} />
                        </IconIduslogo>
                    </SearchHeaderDesktopLogo>
                    {/* 작품, 클래스 */}
                    <SearchHeaderDesktopServiceNav>
                        <ServiceActive>상품</ServiceActive>
                        <Service>클래스</Service>
                    </SearchHeaderDesktopServiceNav>
                    {/* 검색바 */}
                    <SearchInputDesktop>
                        <SearchInputDesktopForm>
                            <HeaderSearch
                                onChange={(e) => {
                                    setWord(e.target.value);
                                    console.log(word);
                                }}
                            ></HeaderSearch>
                            <SearchRelated>
                                <SearchResultUl>
                                    <SearchResultLi>
                                        <SearchResultLiKeyword>첫번째 검색어</SearchResultLiKeyword>
                                    </SearchResultLi>
                                </SearchResultUl>
                            </SearchRelated>
                            {/* 검색 아이콘 */}
                            <HeaderSearchLabel>
                                <SearchInputDesktopSearchButton
                                    type="button"
                                    onClick={() => {
                                        onSubmit();
                                    }}
                                >
                                    <IdusIconSearch className="fa fa-search" />
                                </SearchInputDesktopSearchButton>
                            </HeaderSearchLabel>
                        </SearchInputDesktopForm>
                    </SearchInputDesktop>
                    {/* 실시간 검색 */}
                    <KeywordTrendingDesktop>
                        <TrendingWordSlider>
                            <TrendingSlideWordA>
                                <TrendingSlideWordEm>1</TrendingSlideWordEm>
                                리틀 자수정 목걸이
                            </TrendingSlideWordA>
                        </TrendingWordSlider>
                    </KeywordTrendingDesktop>
                    {/* 내 정보 */}
                    <SearchHeaderProfileLinkNav>
                        <ProfileA type="button" onClick={() => goMypage()}>
                            <IconMypage className="far fa-user" />내 정보
                        </ProfileA>
                        <ProfileCartA href="/cart">
                            {/* <ProfileBadge>0</ProfileBadge> */}
                            <IdusIconCart className="fas fa-shopping-cart">
                                {/* <i class="fas fa-shopping-cart"></i> */}
                            </IdusIconCart>
                            장바구니
                        </ProfileCartA>
                    </SearchHeaderProfileLinkNav>
                </SearchHeaderDesktopBar>
            </SearchHeaderDesktop>

            {/* 카테고리 */}
            <FullGnbScrollDiv>
                <InnerContainerGnb>
                    <UiGnbUl>
                        <UiGnbMenuLi>
                            <MenuSpan
                                onMouseOver={() => setIsCategoryHover(true)}
                                onMouseOut={() => setIsCategoryHover(false)}
                            >
                                카테고리
                            </MenuSpan>
                            <CategorySubmenu
                                onMouseOver={() => setIsCategoryHover(true)}
                                onMouseOut={() => setIsCategoryHover(false)}
                                isCategoryHover={isCategoryHover}
                            >
                                <CategorySubmenuUl>
                                    <CategorySubmenuLi>
                                        <CategorySubmenuA>디저트, 베이커리</CategorySubmenuA>
                                    </CategorySubmenuLi>

                                    <CategorySubmenuLi>
                                        <CategorySubmenuA>음료(커피, 차)</CategorySubmenuA>
                                    </CategorySubmenuLi>

                                    <CategorySubmenuLi>
                                        <CategorySubmenuA>전통주</CategorySubmenuA>
                                    </CategorySubmenuLi>

                                    <CategorySubmenuLi>
                                        <CategorySubmenuA>수제반찬</CategorySubmenuA>
                                    </CategorySubmenuLi>

                                    <CategorySubmenuLi>
                                        <CategorySubmenuA>그외 수제먹거리</CategorySubmenuA>
                                    </CategorySubmenuLi>

                                    <CategorySubmenuLi>
                                        <CategorySubmenuA>농축수산물</CategorySubmenuA>
                                    </CategorySubmenuLi>
                                    <CategorySubmenuLi>
                                        <CategorySubmenuA>의류</CategorySubmenuA>
                                    </CategorySubmenuLi>
                                </CategorySubmenuUl>
                                <CategorySubmenuUl>
                                    <CategorySubmenuLi>시계</CategorySubmenuLi>
                                    <CategorySubmenuLi>섬유, 퀼트</CategorySubmenuLi>
                                </CategorySubmenuUl>

                                <CategorySubmenuUl>
                                    <CategorySubmenuLi>꽃</CategorySubmenuLi>
                                    <CategorySubmenuLi>캔들, 디퓨저, 방향제</CategorySubmenuLi>
                                </CategorySubmenuUl>

                                <CategorySubmenuUl>
                                    <CategorySubmenuLi>휴대폰 케이스</CategorySubmenuLi>
                                    <CategorySubmenuLi>전자기기 관련</CategorySubmenuLi>
                                </CategorySubmenuUl>
                            </CategorySubmenu>
                            <UiGnbSubmenuDiv></UiGnbSubmenuDiv>
                        </UiGnbMenuLi>

                        <UiGnbMenuLi>
                            <MenuA to="/">홈</MenuA>
                        </UiGnbMenuLi>

                        {/* <UiGnbMenuLi>
              <MenuA >오늘의 작품</MenuA>
            </UiGnbMenuLi> */}

                        {/* <UiGnbMenuLi>
              <MenuA>실시간 구매</MenuA>
            </UiGnbMenuLi>

            <UiGnbMenuLi>
              <MenuA>실시간 후기</MenuA>
            </UiGnbMenuLi>

            <UiGnbMenuLi>
              <MenuA>작가님 추천</MenuA>
            </UiGnbMenuLi>

            <UiGnbMenuLi>
              <MenuA>실시간 추천</MenuA>
            </UiGnbMenuLi> */}
                    </UiGnbUl>
                </InnerContainerGnb>
            </FullGnbScrollDiv>
        </HeaderTag>
    );
}

const HeaderTag = styled.header`
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
    font: inherit;
    vertical-align: baseline;
    display: block;

    &:after {
        content: "";
        display: block;
        width: 100%;
        padding-bottom: 40px;
    }
`;
const TopNavigation = styled.div`
    border-top: 0 none;
    //background: #f5f5f5;
    color: #666;
    vertical-align: middle;
    height: 32px;
    width: 100%;
`;
const InnerW = styled.div`
    width: 1056px;
    margin: 0 auto;
    position: relative;
    align-items: center;
    height: 100%;
    justify-content: space-between;
    -webkit-box-align: center;
    display: flex;
`;
const NavBtnUiDropdown = styled.div`
    display: inline-block;
    vertical-align: middle;
    padding: 0 13.5px;
    font-size: 11px;
    position: relative;
`;
const BtnDropdownReset = styled.input`
    cursor: pointer;
    padding: 0;
    background: transparent;
`;
const MenuDropdownAppInstallGuide = styled.section`
    display: none;
    position: absolute;
    width: auto;
    margin-top: 3px;
    margin-left: -33px;
    padding: 8px 12px;
    -webkit-box-shadow: 0 4px 8px 0 rgb(0 0 0 / 10%);
    -moz-box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.1);
    box-shadow: 0 4px 8px 0 rgb(0 0 0 / 10%);
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    border: solid 1px #d9d9d9;
    z-index: 111;
    //background: #fff;
    width: 490px;
    padding: 24px;
    border: 1px solid #333;
    ${(props) =>
            props.isAppInstallHover &&
            css`
                display: block;
            `}

    &:before {
        content: "";
        position: absolute;
        border: 6px solid #d9d9d9;
        border-left-color: transparent;
        border-right-color: transparent;
        border-top-color: transparent;
        top: -12px;
        margin-left: -6px;
        border-bottom-color: #333;
        left: 75px;
    }

    &:after {
        content: "";
        position: absolute;
        border: 4px solid #fff;
        border-left-color: transparent;
        border-right-color: transparent;
        border-top-color: transparent;
        top: -8px;
        margin-left: -4px;
        left: 75px;
    }
`;

const MenuDropdownAppH1 = styled.h1`
    font-size: 18px;
    color: #333;
    border-bottom: 1px solid #333;
    padding-bottom: 13px;
`;

const SpIconImgAppicon = styled.span`
    height: 0;
    overflow: hidden;
    display: inline-block;
    vertical-align: middle;
    background-image: url(${asasasa});
    width: 32px;
    padding-top: 30px;
    margin-right: 8px;
`;

const MenuDropdownAppOl = styled.ol`
    overflow: hidden;
    list-style: none;
`;

const MenuDropdownAppLi = styled.li`
    padding: 4px 0;
    font-size: 11px;
    color: #666;
    float: left;
    width: 149px;

    &:first-child {
        margin-right: 10%;
        width: 238px;
    }
`;

const MenuDropdownAppH2 = styled.h2`
    font-size: 12px;
    color: #333;
    margin: 16px 0 0;
`;

const MenuDropdownAppForm = styled.form`
    margin: 16px 0 7px;
`;

const InputText = styled.label`
    display: inline-block;
    vertical-align: middle;
    cursor: pointer;
`;

const InputCellPhone = styled.input.attrs({
    placeholder: "'-' 없이 번호를 입력하세요",
})`
    -webkit-tap-highlight-color: transparent;
    //background: #fff;
    font-size: 12px;
    line-height: 16px;
    border: 1px solid #acacac;
    box-sizing: border-box;
    padding: 2px 8px;
    border-radius: 2px;
    appearance: none;
    width: 180px;
    height: 29px;
`;

const BtnPoint = styled.button`
    cursor: pointer;
    -webkit-tap-highlight-color: transparent;
    display: inline-block;
    vertical-align: middle;
    box-sizing: border-box;
    border-radius: 2px;
    text-align: center;
    white-space: nowrap;
    padding: 6px 12px;
    font-size: 12px;
    color: #fff;
    background: #02d6e8;
    border: 1px solid #02d6e8;
`;

const MenuDropdownAppSpan = styled.span`
    font-size: 10px;
    color: #666;
`;

const MenuDropdownAppLiTwice = styled.li`
    float: left;
    width: 149px;
    padding: 4px 0;
    font-size: 11px;
    color: #666;
`;

const QrImgicon = styled.img`
    display: block;
    width: 112px;
    height: 112px;
    // background-image: url(https://www.idus.com/resources/dist/images/qrcode.png);
    background-image: url(${QR});
`;

const Fr = styled.nav`
    display: block;
    float: right;
}
`;

const GnbLoginBtn = styled.a`
    display: inline-block;
    vertical-align: middle;
    padding: 0 13.5px;
    font-size: 11px;
`;

const BtnDropdown = styled.button`
    height: 20px;
    display: flex;
