`;

const SectionTopMore = styled.button`
  position: absolute;
  right: 200px;
  color: #9971ff;
  padding: 8px 15px;
  border: 2px solid #9971ff;
  border-radius: 30px;
  background: transparent;
  font-weight: 700;
  font-size: 15px;
  transition: 0.3s;

  &:hover {
    color: white;
    background: #9971ff;
    transition: 0.3s;
  }
`;

export default Main;
