import React from 'react';
import {Link, NavLink, useLocation} from 'react-router-dom';
import styled from "styled-components";
import top from "../images/top.png";
import SideBar from "../SideBar";
import axios from "axios";
import edit from "../images/edit.svg";
import play from "../images/play.svg";


import {useState} from 'react';


//MyPage
const MyPage = () => {


    const [currentTab, clickTab] = useState(0);


    const menuArr = [{name: '시청내역', content: ''}, {name: '찜', content: ''}, {
        name: '작성 리뷰',
        content: ''
    }, {name: '개인정보 수정', content: ''}, {name: '이용권', content: ''},];

    const selectMenuHandler = (index) => {
        clickTab(index);
    };


    const pathName = useLocation().pathname;

    //
    let body = {};

    //
    const onSubmit = async () => {
        try {
            const response = await axios.post("https://backend.alittlevanilla.kro.kr/member/signup", body, {
                headers: {"Content-Type": "application/json"},
            });
            console.log(response.data.code);
            if (response.data.code === 1000) {
                window.location.href = "/emailcheck";
            }
        } catch (e) {
            console.log(e);
        }
    };


    return (<>
            <SideBar/>
            <Wrap>

                {/*상단- 프로필*/}
                <TopContainer>
                    <Section>
                        <ProfileBoxContainer>
                            <TopImg src={top}></TopImg>

                            <TitelBox>홍길동 님
                                <img
                                    className="profile-img"
                                    src={edit}/>
                            </TitelBox>

                            <FormBlockBody>
                                <InputTextSizeW>
                                    <EmailsButton
                                        type="button"
                                        onClick={() => {
                                            onSubmit();
                                        }}
                                    >
                                        프로필 전환
                                    </EmailsButton>
                                </InputTextSizeW>
                            </FormBlockBody>
                        </ProfileBoxContainer>

                        <Titel>나의 이용권 &nbsp;&nbsp;&nbsp;&nbsp;|&nbsp;&nbsp;&nbsp;&nbsp;스탠다드 정기권
                            <img
                                className="play-img"
                                src={play}/>
                        </Titel>
                    </Section>
                </TopContainer>


                {/*바디- 탭구현*/}
                <WrapBody>
                    <WrapTabMenu>
                        <TabMenu>
                            {menuArr.map((el, index) => (


                                <li
                                    className={index === currentTab ? "submenu focused" : "submenu"}
                                    onClick={() => selectMenuHandler(index)}>{el.name}

                                </li>))}

                            <button className="click_on"></button>
                        </TabMenu>
                    </WrapTabMenu>

                    {/*<Div>*/}
                    {/*    <p>{menuArr[currentTab].content}</p>*/}
                    {/*    <form onSubmit={handleFindId}>*/}
                    {/*        <label htmlFor="memName">이름</label>*/}
                    {/*        <input*/}
                    {/*            type="text"*/}
                    {/*            id="memName"*/}
                    {/*            name="memName"*/}
                    {/*            className='commonpage'*/}
                    {/*            value={findIdForm.memName}*/}
                    {/*            onChange={handleFindIdChange}*/}
                    {/*        /><br/>*/}
                    {/*        <label htmlFor="memEmail">이메일</label>*/}
                    {/*        <input*/}
                    {/*            type="email"*/}
                    {/*            id="memEmail"*/}
                    {/*            name="memEmail"*/}
                    {/*            className='commonpage'*/}
                    {/*            value={findIdForm.memEmail}*/}
                    {/*            onChange={handleFindIdChange}*/}
                    {/*        /><br/>*/}
                    {/*        <button type="submit"*/}
                    {/*                className='loginbutton'>아이디 찾기*/}
                    {/*        </button>*/}
                    {/*    </form>*/}
                    {/*</Div>*/}
                </WrapBody>
            </Wrap>
        </>);
}
export default MyPage;


const WrapBody = styled.div`
    //background-color: #fff;
    width: 600px;
    //margin: 0 auto;
    display: flex;
    justify-content: left;

    -webkit-box-sizing: border-box;
    //background-color: #fff;
`;

const WrapTabMenu = styled.div`
    display: flex;
    justify-content: center;
    //max-width: 1200px;
    padding: 20px 60px;
    margin-top: 20px;
`;
//탭메뉴
const TabMenu = styled.ul`
    list-style: none;
    margin-bottom: 20px;
    margin-top: 10px;
    display: flex;
    flex-direction: row;
    align-items: center;

    //디자인- 비활성화
    color: #000;
    font-weight: 300;
    text-align: center;
    display: flex;
    justify-content: center;
    font-size: 16px;


    //클릭 온
    //.click_on{
    //    position: absolute;
    //    width: 100px;
    //    height: 2px;
    //    background-color: rgb(255, 255, 255);
    //}
    //탭메뉴

    .submenu {
        // 기본 Tabmenu 에 대한 CSS를 구현
        width: 140px;
        heigth: 30px;
        padding: 10px;
        transition: 0.5s;

        border-bottom: 1px solid rgb(46, 46, 46);
        //border: 1px solid rgba(255, 255, 255, 0.2);

        outline: none;
        cursor: pointer;
        color: #a6a6a6;
        font-weight: 300;
        text-align: center;
        display: flex;
        justify-content: left;
        font-size: 18px;

        &:hover {
            color: #fff;
            border-bottom: 2px solid #fff;
        }

        &:focus {
            color: #fff;
            border-bottom: 2px solid #fff;
        }
    }
`;


//
// const Tabs = styled.div`
//     margin-top: 50px;
//     padding-bottom: 40px;
//
//     box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
//     width: 700px;
//     margin: 0 auto;
//
// `;


//전체 박스
const Wrap = styled.div`
    width: 100%;
    height: 100vh;
`;


//전체 감싸는 프로필 박스
const TopContainer = styled.div`
    width: 100%;
    //height: 280px;
    //position: relative;
    grid-template-columns: 10rem minmax(auto, 100%) min-content;
    column-gap: 2rem;
    padding: 40px 40px;
    background-color: rgb(25, 25, 25);
`;


// 프로필 감싸는 박스
const Section = styled.div`
    width: 1024px;
    //margin: 0 auto;
    //display: flex;
    //justify-content: flex-start;
    //margin-top: 22px;
`;

// 프로필 박스
const ProfileBoxContainer = styled.div`
    //width: 600px;
    display: flex;
    align-items: flex-start;
    //gap: 30px;
    border-right: 1px solid #1b1b1b;
    align-items: center;
`;


//나의 이용권-글씨
const Titel = styled.div`

    width: 400px;
    margin-top: 10px;

    position: absolute;
    left: 11em;
    top: 11.5em;
    font-size: 20px;
    color: #a3a3a3;
    font-family: 'SUIT-Regular' !important;
    text-align: left;
    font-weight: 500;

    .play-img {
        margin -left: 10px;
        left;
        margin -right: 10px;
        30px;
        0;
        //padding: 2px 2px;
        margin -bottom: 5px;
        text -align: center;
        line -height: 30px;
    }
`;


// 프로필
const TopImg = styled.img`
    width: 160px;
    margin-right: 30px;
`;

//홍길동 박스
const TitelBox = styled.div`
    width: 160px;
    color: #fff;
    font-family: 'SUIT-Regular' !important;
    font-size: 2rem;
    margin-bottom: 40px;
    text-align: left;
    font-weight: 500;
    font-stretch: normal;
    font-style: normal;
    letter-spacing: normal;
    text-align: left;
    z-index: 1;
    //margin-top: 65px;
    margin-right: 20px;
    align-items: center;
    position: relative;

    .profile-img {
        margin -left: 10px;
        1;
        40px;
        absolute;
        0;
        2px
        2px;
        margin -bottom: 5px;
    }

`;


//프로필 전환 버튼
const EmailsButton = styled.button`
    width: 100px;
    height: 45px;

    padding: 0.5rem 0.833rem;
    font-size: 12px;
    text-align: center;
    background-color: transparent !important;
    border: 1px solid rgb(77, 77, 77);
    opacity: 0.7;
    color: #f4f4f4;
    margin-top: 1.8rem;
    border-radius: 2px;
    //margin-left: 20px;
    float: left;


    &:hover {
        solid 1px # 1351 f9;
        color: # 1351 f9;
    }

    &:active {
        solid
        1px #ff27a3;

        #ff27a3
    ;
    }
`;

// 전체 폼 박스
const FormBlockBody = styled.div`
    text-align: left;
    outline: none;
    align-items: center;
    margin-bottom: 5em;

`;

// 사용자 작성 구간
const InputTextSizeW = styled.div`
    text-align: left;
    vertical-align: middle;
    box-sizing: border-box;
    outline: none;
`;
