import {useState} from 'react';
import styled from 'styled-components';
import {useNavigate} from "react-router-dom";
import axios from "axios";




//아이디&비밀번호 재설정
export const FindIdPassword = () => {


    //탭
    const tabContArr=[
        {
            tabTitle:(
                <li> 탭1 </li>
            ),
            tabCont:(
                <div> 탭1 내용 </div>
            )
        },
        {
            tabTitle:(
                <li> 탭2 </li>
            ),
            tabCont:(
                <div> 탭2 내용 </div>
            )
        }
    ];

//html --------------------------
    return (
        <WrapBody>
            <Wrap>
                <TabMenu>
                    {/*// 아래 하드코딩된 내용 대신에, map을 이용한 반복으로 코드를 수정*/}
                    {/*// li 엘리먼트의 class명의 경우 선택된 tab 은 'submenu focused', 나머지 2개의 tab은 'submenu'*/}


                    {/*<li className="submenu">{menuArr[0].name}</li>*/}
                    {/*<li className="submenu">{menuArr[1].name}</li>*/}
                    {/*<li className="submenu">{menuArr[2].name}</li>*/}

                    {menuArr.map((el, index) => (

                        <li
                            className={index === currentTab ? "submenu focused" : "submenu"}
                            onClick={() => selectMenuHandler(index)}>{el.name}
                        </li>
                    ))}
                </TabMenu>

                {/* ----- 폼 ---------*/}
                <Nav.Item>
                    <Nav.Link onClick={()=>{setTab(0)}}></Nav.Link>
                </Nav.Item>

            </Wrap>

        </WrapBody>
        );
}
export default FindIdPassword;


// style --------------------------
const WrapBody = styled.div`
    //background-color: #fff;
    width: 100%;
    margin: 0;
    padding: 0;
    overflow-x: hidden;
    -webkit-box-sizing: border-box;
`;


// 전체 박스
const Wrap = styled.div`
    display: flex;
    justify-content: center;
    //max-width: 1200px;
    width: 1200px;
    margin: 0 auto;
    padding: 60px 40px;
    margin-top: 20px;
`;


//탭메뉴
const TabMenu = styled.ul`
    list-style: none;
    margin-bottom: 20px;
    margin-top: 10px;
    display: flex;
    flex-direction: row;
    align-items: center;


    //디자인- 비활성화
    color: #000;
    font-weight: 300;
    text-align: center;
    display: flex;
    justify-content: center;
    font-size: 16px;

    //탭메뉴

    .submenu {
        // 기본 Tabmenu 에 대한 CSS를 구현
        width: 300px;
        heigth: 30px;
        padding: 10px;
        transition: 0.5s;
        border: 1px solid rgba(255, 255, 255, 0.2);
        outline: none;
        cursor: pointer;
        color: #fff;
        font-weight: 300;
        text-align: center;
        display: flex;
        justify-content: center;
        font-size: 16px;
    }

    //활설화

    .focused {
        //선택된 Tabmenu 에만 적용되는 CSS를 구현
        background-color: #1351f9;
        //background-color: rgba(11, 11, 13);
        color: #fff;
        font-weight: 300;
        text-align: center;
        display: flex;
        justify-content: center;
        font-size: 16px;

        &:focus {
            display: flex;
            justify-content: center;
            width: 1200px;
            height: 400px;
            margin: 0 auto;
            background-color: red;
            margin-bottom: 20px;
            padding: 80px 40px;
        }
    }

    & div.desc {

    }
`;

const Div = styled.div`
    display: flex;
    justify-content: center;
    width: 1200px;
    height: 400px;
    margin: 0 auto;
    background-color: red;
    margin-bottom: 20px;
    padding: 80px 40px;



`;
