import React from 'react';
import { Link, NavLink } from 'react-router-dom';
// import * as S from './style';
import Router from "../../Router";
import styled from "styled-components";



const MyPage = () => {
    const activeStyle = {
        color: '#289951',
        fontWeight: 700,
    };
    return (
        <Wrap>
            <Side>
                <div className="side">
                    <p className="side_span">MY PAGE</p>
                    마이페이지
                </div>
            </Side>

                <SideWrap>

                    <Menu>
                        <NavLink style={({ isActive })=> (isActive ? activeStyle : {})} to='/mypage'>
                            내가 찜한 리스트
                        </NavLink>
                    </Menu>

                    <Menu>
                        <NavLink style={({ isActive }) => (isActive ? activeStyle : {})} to='/mypage-pw'>
                            내가 작성한 리뷰
                        </NavLink>
                    </Menu>

                    <Menu>
                        <Link to='/mypage-od'>개인 정보 수정</Link>
                    </Menu>

                    <Menu>
                        <Link to='/logout'>로그아웃</Link>
                    </Menu>
            </SideWrap>
        </Wrap>
    );
};
export default MyPage;


// 폼 디자인
const Wrap = styled.div`
    width: 100%;
    height: 100vh;
    background-color: #fff;
`;

const SideWrap = styled.div`
    width: 240px;
    height: 1080px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 120px 40px;
    border-right: 1px solid #f4f4f4;
    background-color: #fff;
`;


// 사이드 바 - 스타일
const Side = styled.div`
    width: 240px;
    height: 90px;
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    margin-top: 40px;
    background-color: #1351f9;
    position: fixed;
    padding: 20px 0px;
    
    .side_span{
        color: #fff;
        font-family: 'SUIT-Regular' !important;
        text-align: left;
        font-weight: 500;
        font-size: 12px;
    }
    .side{
        margin: 0;
        font-family: 'SUIT-Regular' !important;
        font-size: 24px;
        font-weight: 300;
        //border-bottom: 3px solid #333;
        padding: 0 25px;
        font-weight: 300;
        color: #fff;
        text-align: left;
    }
`;




const Menu = styled.div`
    margin-top: 30px;
    width: 150px;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    font-family: 'SUIT-Regular' !important;
    font-size: 14px;
    font-weight: 500;
    cursor: pointer;
    text-align: left;
    color: #0f2027 ! important;
`;



const Profile = styled.img`
    width: 135px;
    //height: 90px;
    border-radius: 100%;
    margin-bottom: 40px;
`;
