import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
// import '../../common/css/FindIdPassword.css';



//ID 찾기
function FindLogin() {

    const navigate = useNavigate();

    const [findIdForm, setFindIdForm] = useState({
        memName: '',
        memEmail: ''
    });

    // const [username, setUsername] = useState("");
    const [message, setMessage] = useState('');

    // 아이디 찾기 폼 관련
    const handleFindIdChange = (e) => {
        setFindIdForm({ ...findIdForm, [e.target.name]: e.target.value });
    };

    // 아이디 찾기 로직
    const handleFindId = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('/auth/find-id', findIdForm);
            alert(`아이디: ${response.data}`);
        } catch (error) {
            alert('회원정보를 찾지 못했습니다.');
        }
    };


    //HTML
    return(
        <EmailLoginContainer>

            <Form onSubmit={handleFindId}>

                <label htmlFor="memName">이름</label>
                <EmailLoginInput
                    placeholder="이름"
                    type="text"
                    id="memName"
                    name="memName"
                    value={findIdForm.memName}
                    required
                    onChange={handleFindIdChange}
                />

                <label htmlFor="memEmail">이메일</label>
                <EmailLoginInput
                    placeholder="이메일"
                    type="email"
                    id="memEmail"
                    name="memEmail"
                    className='commonpage'
                    value={findIdForm.memEmail}
                    required
                    onChange={handleFindIdChange}
                />

                <Button
                    type="submit"
                    className='loginbutton'>아이디 찾기
                </Button>

            </Form>
        </EmailLoginContainer>
    );
}

export default FindLogin;


// 폼 디자인
const EmailLoginContainer = styled.div`
    float: left;
    width: 560px;
    padding: 0 40px;
`;
//폼 박스
const Form = styled.form`
    float: left;
    width: 560px;
    padding: 0 40px;
`;

// 인풋 디자인
const EmailLoginInput = styled.input`
    width: 100%;
    font-size: 15px;
    font-family: 'SUIT-Regular' !important;
    font-weight: 400;
    height: 53px;
    color: #fff;
    background-color: #2f2f2f;
    border: 0;
  border-radius: 5px;
  margin-bottom: 12px;
  display: flex;
  justify-content: center;
  padding: 0 15px;
  outline: none;
    
  &:last-child {
    margin-bottom: 0;
  }
`;
