import React from "react";
import styled from 'styled-components';

import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";




const StyledSlider =  styled (Slider)`
    .slick-next {
        color: black;
        position: relative;
        top: 7vh;
        right: 2vw;
    }

    .slick-prev {
        color: black;
        left: 0;
        position: relative;
        top: 7vh;
    }

    .slick-list {
        width: 80%;
        padding: 1;
        right: -20px;
    }
    .box{
        width: 350px;
        height: 350px;
    }
`;


const SimpleSlider = () => {
    // const settings = {
    //     dots: true,
    //     infinite: true,
    //     speed: 500,
    //     slidesToShow: 1,
    //     slidesToScroll: 1,
    // };

    return (
        <StyledSlider {...settings}>
        <div className="box">
            <h2> Simple Slider </h2>
            <Slider {...settings}>
                <div>
                    <h3>1</h3>
                </div>
                <div>
                    <h3>2</h3>
                </div>
                <div>
                    <h3>3</h3>
                </div>
                <div>
                    <h3>4</h3>
                </div>
                <div>
                    <h3>5</h3>
                </div>
                <div>
                    <h3>6</h3>
                </div>
            </Slider>
        </div>
        </StyledSlider>
    );
};

export default SimpleSlider;
