import React from 'react';
import {Link, NavLink, useLocation} from 'react-router-dom';

// import axios from "axios";
// import React, { useState, useEffect } from "react";

import styled from "styled-components";
import top from "./images/top.png";
import SideBar from "./SideBar";
import {Button} from "react-bootstrap";


function MyPage() {

    const pathName = useLocation().pathname;

    return (<>
            <SideBar/>
            <Wrap>

                    <TopContainer>
                        {/*<img className="top-img" src={Mypage}/>*/}
                        <Section>
                            <Article>
                                    <TopImg src={top}>
                                    </TopImg>


                                <TitelBoxContainer>
                                    <TitelBox>홍길동</TitelBox>

                                    <FormBlockBody>
                                        <InputTextSizeW>
                                            <EmailsButton
                                                type="button"
                                                onClick={() => {
                                                    onSubmit();
                                                }}
                                            >
                                                프로필 전환
                                            </EmailsButton>

                                        </InputTextSizeW>
                                    </FormBlockBody>

                                    <TitelBox>나의 이용권</TitelBox>

                                </TitelBoxContainer>
                            </Article>
                        </Section>
                    </TopContainer>



                {/*<MainContainer>*/}
                {/*    <TitleNo>내가 찜한 리스트</TitleNo>*/}
                {/*    <TitelBox></TitelBox>*/}
                {/*</MainContainer>*/}

                {/*<MainContainer>*/}
                {/*    <TitleNo>내가 작성한 리뷰</TitleNo>*/}
                {/*    <TitelBox></TitelBox>*/}
                {/*</MainContainer>*/}

                {/*<MainContainer>*/}
                {/*    <TitleNo>개인 정보 수정</TitleNo>*/}
                {/*    <TitelBox></TitelBox>*/}
                {/*</MainContainer>*/}
            </Wrap>
        </>);
}

export default MyPage;


//전체 박스
const Wrap = styled.div`
    width: 100%;
    height: 100vh;
`;
//전체 감싸는 프로필 박스
const TopContainer = styled.div`
    width: 100%;
    height: 180px;
    //margin: 0 auto;
    position: relative;
    grid-template-columns: 10rem minmax(auto, 100%) min-content;
    column-gap: 2rem;
    padding: 40px 40px;
    background-color: rgb(25, 25, 25);
`;

// 프로필 감싸는 박스
const Section = styled.div`
    width: 1024px;
    margin: auto;

`;
// 프로필 박스
const Article = styled.div`
    width: 1024px;
    display: flex;
    justify-content: left;

`;
// 프로필
const TopImg = styled.img`
    padding-left: 20px;
    padding-right: 20px;
    width: 120px;
   float: left;
`;
//문구 감싸는 박스
const TitelBoxContainer = styled.div`
    
`;
const EmailsButton = styled.button`
    width: 100px;
    height: 50px;
    //margin-right: 20px;
    text-align: center;
    //background-color: #1351f9;
    background-color: transparent !important;
    border: solid 1px #f4f4f4 !important;
    color: #f4f4f4 !important;
    /* border: 1px solid red; */

    &:hover{
        border: solid 1px #1351f9;
        color: #1351f9;
    }

    &:active{
        border: solid 1px #ff27a3;
        color: #ff27a3;
    }
`;

// 전체 폼 박스
const FormBlockBody = styled.div`
  text-align: left;
    margin: 20px 0 0;
    outline: none;
`;
// 사용자 작성 구간
const InputTextSizeW = styled.div`
    
  &.formError {
    cursor: default !important;
  }
  display: block;
  width: 100%;
  margin-top: 10px;
  text-align: left;
  vertical-align: middle;
  box-sizing: border-box;
    outline: none;
`;


const TitleNo = styled.div`
    width: 200px;
   
    color: rgba(255, 255, 255, 1);
   
   
    
`;


const TitelBox = styled.div`
    width: 1024px;

    color: #fff;
    font-family: 'SUIT-Regular' !important;
    font-size: 1.2rem;
    font-weight: 500;
    line-height: 1.5;
    font-stretch: normal;
    font-style: normal;
    letter-spacing: normal;
    text-align: left;
    z-index: 1;
`;
